import {CiPai} from "../components/model";

const allCiPai: CiPai[] = [{"id": 1, "name": "一七令", "description": "按计敏夫《唐诗纪事》，白乐天分司东洛，朝贤悉会兴化池亭送别，酒酣，各请一字至七字诗，以题为韵，后遂沿为词调。"}, {"id": 2, "name": "一丛花", "description": "调见《东坡词》，有欧阳修、晁补之、秦观、程垓词可校。"}, {"id": 3, "name": "一剪梅", "description": "元高拭词注“南吕宫”。周邦彦词起句有“一剪梅花万样娇”句，取以为名。韩淲词有“一朵梅花百和香”句名《腊梅香》。李清照词有“红藕香残玉簟秋”句，名《玉簟秋》。"}, {"id": 4, "name": "一叶落", "description": "《五代史》云：“后唐庄宗能自度曲。”此其一也。取首句为调名。"}, {"id": 5, "name": "一寸金", "description": "调见柳永词。"}, {"id": 6, "name": "一斛珠", "description": "《宋史·乐志》名《一斛夜明珠》，属中吕调。《尊前集》注“商调”，金词注“仙吕调”，蒋氏《九宫谱目》入仙吕引子。晏几道词名《醉落魄》，张先词名《怨春风》，黄庭坚词名《醉落拓》。"}, {"id": 7, "name": "一枝春", "description": "调见杨缵词，其自度曲也。"}, {"id": 8, "name": "一萼红", "description": "此调有平韵、仄韵两体。平韵者见姜夔词，仄韵者见《乐府雅词》。因词有“未教一萼，红开鲜蕊”句，取以为名。"}, {"id": 9, "name": "一落索", "description": "欧阳修词名《洛阳春》，张先词名《玉连环》，辛弃疾词名《一络索》。"}, {"id": 10, "name": "丁香结", "description": "调见《清真集》。古诗有“丁香结恨新”，调名本此。"}, {"id": 11, "name": "七孃子", "description": "蒋氏《九宫谱目》入正宫引子。"}, {"id": 12, "name": "万年欢", "description": "唐教坊曲名。《宋史·乐志》：“中吕宫”。《高丽史·乐志》名《万年欢慢》，《元史·乐志》：“舞队曲”。此调有三体，平韵者始自王安礼，仄韵者始自晁补之，平仄韵互叶者始自元赵孟頫。"}, {"id": 13, "name": "万里春", "description": "调见周邦彦《片玉词》。《清真集》不载，故方千里、杨泽民、陈允平俱无和词。"}, {"id": 14, "name": "三台", "description": "唐教坊曲名。宋李济翁《资暇录》：《三台》，今之啐酒三十拍促曲，啐，送酒声也。宋张表臣《珊瑚鈎诗话》：乐部中有促拍催酒，谓之《三台》。沈括词名《开元乐》，因结有“翠华满陌东风”句，名《翠华引》。"}, {"id": 15, "name": "三台大曲", "description": "见唐《教坊记》。《唐音统籖》云：“唐曲有《三台》：《急三台》、《宫中三台》、《上皇三台》、《怨陵三台》、《突厥三台》，《三台》为大曲。”冯鉴《续事始》曰：“汉蔡邕三日之间，周历三台，乐府以邕晓音律，为制此曲。”刘禹锡《嘉话录》曰：“邺中有曹公铜雀、金虎、冰井三台，北齐高洋毁之，更筑金凤、圣应、崇光三台，宫人拍手呼上台送酒，因名其曲为《三台》。”李氏《资暇录》曰：“三台，三十拍促曲名。昔邺中有三台，石季龙常为宴游之所，而造此曲以促饮。”《乐苑》云：“唐《三台》，羽调曲。”"}, {"id": 16, "name": "三奠子", "description": "调见元好问《锦机集》。按崔令钦《教坊记》有《奠璧子》小曲，此或即奠酒、奠声、奠璧为三奠，取以名词也。"}, {"id": 17, "name": "三姝媚", "description": "调见《梅溪集》。"}, {"id": 18, "name": "三字令", "description": "调见《花间集》。前后段俱三字句，故名。"}, {"id": 19, "name": "三登乐", "description": "调见《石湖词》。按《汉书·食货志》：“三考黜陟，余三年食，进业曰登。再登曰平，余六年食。三登曰泰平，二十七岁，遗九年食。然后王德流洽，礼乐成焉。”《三登乐》之调名取此。"}, {"id": 20, "name": "三部乐", "description": "调见《东坡词》。按《唐书·礼乐志》：“明皇分乐为二部，堂下立奏，谓之立部伎；堂上坐奏，谓之坐部伎。又酷爱法曲，选坐部伎子弟三百，教于梨园，为法曲部。”三部之名，疑出于此。"}, {"id": 21, "name": "上林春令", "description": "《宋史·乐志》属中吕宫。"}, {"id": 22, "name": "上林春慢", "description": "《宋史·乐志》：“中吕宫”。"}, {"id": 23, "name": "上行杯", "description": "唐教坊曲名。"}, {"id": 24, "name": "下水船", "description": "唐教坊曲名。按唐王保定《摭言》：裴庭裕，乾宁中在内庭，文书敏捷，号“下水船”。调名取此。"}, {"id": 25, "name": "且坐令", "description": "调见《东浦词》。"}, {"id": 26, "name": "东坡引", "description": "此调前后段两结，宋人类用叠句，惟曹冠、袁去华词二首独无，旧谱遗之，今并增定。"}, {"id": 27, "name": "东风第一枝", "description": "蒋氏《九宫谱》注“大石调”。"}, {"id": 28, "name": "东风齐著力", "description": "调见《草堂诗余》，胡浩然《除夕》词也。按《礼记·月令》：“孟春之月，东风解冻。”又唐人曹松《除夜》诗：“残腊即又尽，东风应渐闻。”故云《东风齐著力》。"}, {"id": 29, "name": "两同心", "description": "此调有三体，仄韵者创自柳永，《乐章集》注“大石调”。平韵者创自晏几道。三声叶韵者创自杜安世。"}, {"id": 30, "name": "中兴乐", "description": "见《花间集》。牛希济词有“泪湿罗衣”句，名《湿罗衣》。"}, {"id": 31, "name": "临江仙", "description": "唐教坊曲名。《花庵词选》云，唐词多缘题所赋，《临江仙》之言水仙，亦其一也。宋柳永词注“仙吕调”，元高拭词注“南吕调”。李煜词名《谢新恩》。贺铸词有“人归落雁后”句，名《雁后归》。韩淲词有“罗帐画屏新梦悄”句，名《画屏春》。李清照词有“庭院深深深几许”句，名《庭院深深》。"}, {"id": 32, "name": "临江仙引", "description": "调见《乐章集》，注“南吕调”。与《临江仙令》、《临江仙慢》不同。"}, {"id": 33, "name": "临江仙慢", "description": "《乐章集》注“仙吕调”。"}, {"id": 34, "name": "丹凤吟", "description": "调见《清真乐府》。"}, {"id": 35, "name": "乌夜啼", "description": "唐教坊曲名。《太和正音谱》注“南吕宫”，又“大石调”。宋欧阳修词名《圣无忧》，赵令畤词名《锦堂春》。按郭茂倩《乐府诗集》有清商曲《乌夜啼》，乃六朝及唐人古今诗体，与此不同，此盖借旧曲名，另翻新声也。"}, {"id": 36, "name": "乾荷叶", "description": "元刘秉忠自度曲。属南吕宫，取起句三字为调名。"}, {"id": 37, "name": "二色宫桃", "description": "调见《梅苑》。其句读近《玉阑干》，而平仄不同。"}, {"id": 38, "name": "二色莲", "description": "调见《松隐集》，即咏《二色莲》。"}, {"id": 39, "name": "二郎神", "description": "唐教坊曲名。《乐章集》注“双调”。徐伸词名《转调二郎神》。吴文英词名《十二郎》。"}, {"id": 40, "name": "于飞乐", "description": "金词注“高平调”。元词注“南吕调”。史达祖词名《鸳鸯怨曲》。"}, {"id": 41, "name": "云仙引", "description": "冯伟寿自度曲，原注“夹钟商”。"}, {"id": 42, "name": "五福降中天", "description": "调见《花草粹编》，一作《五福降中天慢》。"}, {"id": 43, "name": "五綵结同心", "description": "此调有平韵、仄韵两体。平韵者见赵彦端《介庵词》。仄韵者见《乐府雅词》。"}, {"id": 44, "name": "人月圆", "description": "《中原音韵》注“黄钟宫”。此调始于王诜，因词中“人月圆时”句，取以为名。吴激词有“青衫泪湿”句，又名《青衫湿》。"}, {"id": 45, "name": "伊州三台", "description": "按唐有《宫中三台》、《江南三台》等曲，此云“伊州”者，亦本唐曲，取边地为名也。《三台》皆用六字成句，观赵师侠词前后起两句亦作六言，犹沿唐人旧体。若两结摊破六字二句为五字一句、七字一句，则新声矣，故另编一体。"}, {"id": 46, "name": "伊州令", "description": "唐教坊曲名，一作《伊川令》。《碧鸡漫志》云：“伊州有七商曲。”"}, {"id": 47, "name": "传言玉女", "description": "高拭词注“黄钟宫”。按《汉武内传》，帝閒居承华殿，忽见一女子曰：“我墉宫玉女王子登也。至七月七日，王母暂来。”言讫，不知所在。世所谓传言玉女也。调名取此。"}, {"id": 48, "name": "伤春怨", "description": "见《能改斋漫录》，王安石梦中作。"}, {"id": 49, "name": "佳人醉", "description": "《乐章集》注“双调”。"}, {"id": 50, "name": "使牛子", "description": "调见曹冠《燕喜词》。"}, {"id": 51, "name": "侍香金童", "description": "金词注“黄钟宫”，又“黄钟调”。按《开天遗事》，王元宝常于寝帐牀前，雕矮童二人，捧一宝博山炉，自暝焚香彻晓，调名取此。无名氏词即咏其事也。"}, {"id": 52, "name": "侧犯", "description": "陈旸《乐书》云：“唐自天后末年，剑气入浑脱，始为犯声，明皇时，乐人孙处秀，善吹笛，好作犯声，时人因为新意而效之，因有犯调。”姜夔词注云：“唐人《乐书》，以宫犯羽者为侧犯。”此调创自周邦彦，调名或本于此。"}, {"id": 53, "name": "促拍满路花", "description": "此调有平韵、仄韵二体。平韵者始自柳永，《乐章集》注“仙吕调”。仄韵者始自秦观，或名《满路花》，无“促拍”二字。秦观词一名《满园花》，周邦彦词名《归去难》，袁去华词名《一枝花》，牛真人词名《喝马一枝花》。《太平乐府》注“南吕调”。"}, {"id": 54, "name": "促拍采桑子", "description": "调见朱希真《太平樵唱词》，一名《促拍丑奴儿》。促拍者，即促节繁声之意，《中原音韵》所谓“急曲子”也，字句与《采桑子》、《添字采桑子》迥别。"}, {"id": 55, "name": "保寿乐", "description": "周密《天基圣节乐次》：“再坐第六盏，觱篥独吹商角调筵前《保寿乐》。”"}, {"id": 56, "name": "倒垂柳", "description": "唐教坊曲名。"}, {"id": 57, "name": "倒犯", "description": "调始《清真乐府》，一名《吉了犯》。"}, {"id": 58, "name": "倚西楼", "description": "调见《苕溪诗话》，因词有“西楼萧瑟有谁知”句，取以为名。"}, {"id": 59, "name": "倚阑人", "description": "调见《松隐集》，曹勛自度曲。"}, {"id": 60, "name": "倦寻芳", "description": "王雱词注“中吕宫”。潘元质词名《倦寻芳慢》。"}, {"id": 61, "name": "倾杯乐", "description": "唐教坊曲名。《乐府杂录》云：“《倾杯乐》，宣宗喜吹芦管，自制此曲。”见《宋史·乐志》者二十七宫调，柳永《乐章集》注“宫调七”。一名《古倾杯》，亦名《倾杯》。"}, {"id": 62, "name": "倾杯令", "description": "唐教坊曲有《倾杯乐》，调名本此。但此令词与慢词名《倾杯乐》者不同。"}, {"id": 63, "name": "倾杯近", "description": "调见袁去华集。与《倾杯令》、《倾杯乐》二体不同。"}, {"id": 64, "name": "偷声木兰花", "description": "此调亦本于《木兰花令》，前后段第三句减去三字，另偷平声，故云“偷声”。若《减字木兰花》前后段起句四字，则又从此调减去三字耳。"}, {"id": 65, "name": "催雪", "description": "此调始自姜夔，本催雪词也，即以为名。吴文英、王沂孙俱有此调词。与《无闷》调不同，《词律》类列者误。"}, {"id": 66, "name": "兀令", "description": "调见《东山集》。"}, {"id": 67, "name": "入塞", "description": "古乐府横吹曲有《入塞辞》，调名本此。"}, {"id": 68, "name": "八六子", "description": "秦观词有“黄鹂又啼数声”句，又名《感黄鹂》。"}, {"id": 69, "name": "八声甘州", "description": "《碧鸡漫志》：“《甘州》，仙吕调，有曲破，有八声，有慢，有令。”按此调前后段八韵，故名“八声”，乃慢词也，与《甘州遍》之曲破，《甘州子》之令词不同。《乐章集》亦注“仙吕调”。周密词名《甘州》。张炎词因柳词有“对萧萧暮雨洒江天”句，更名《萧萧雨》。白朴词名《讌瑶池》。"}, {"id": 70, "name": "八宝妆", "description": "仇远词名《八宝玉交枝》，与《新雁过妆楼》别名《八宝妆》者不同。"}, {"id": 71, "name": "八归", "description": "此调有仄韵、平韵两体。仄调者见《白石词》，姜夔自度夹钟商曲。平韵者见《竹屋痴语》，高观国自度曲。"}, {"id": 72, "name": "八拍蛮", "description": "唐教坊曲名。按孙光宪词所咏俱越中事，或即八拍之蛮歌也。"}, {"id": 73, "name": "八节长欢", "description": "调见《东堂词》。"}, {"id": 74, "name": "八音谐", "description": "调见曹勛《松隐集》，自注“以八曲声合成，故名。”"}, {"id": 75, "name": "六丑", "description": "调见《清真乐府》。"}, {"id": 76, "name": "六州", "description": "《文献通考》：“本朝歌吹，止有四曲：《十二时》、《导引》、《降仙台》，并《六州》为四。每大礼宿斋或行幸，遇夜每更三奏，名为‘警场’。政和七年，诏《六州》改名《崇明祀》，然天下仍谓之《六州》，其称谓已熟也。”"}, {"id": 77, "name": "六州歌头", "description": "程大昌《演繁露》：“《六州歌头》，本鼓吹曲也，近世好事者倚其声为弔古词，音调悲壮，又以古兴亡事实文之，闻其歌，使人慷慨，良不与艳词同科，诚可喜也。”"}, {"id": 78, "name": "六幺令", "description": "《碧鸡漫志》：“《六幺》一名《绿腰》，一名《乐世》，一名《录要》。或云此曲拍无过六字者，故曰《六么》。今《六么》行于世者，曰黄钟羽，即俗呼般涉调；曰夹钟羽，即俗呼中吕调；曰林钟羽，即俗呼高平调；曰夷则羽，即俗呼仙吕调，皆羽调也。”"}, {"id": 79, "name": "六花飞", "description": "调见《松隐集》。"}, {"id": 80, "name": "兰陵王", "description": "唐教坊曲名。《碧鸡漫志》：“《北齐史》及《隋唐嘉话》称，齐文襄之长子长恭封兰陵王，与周师战，尝著假面对敌。击周师金墉城下，勇冠三军，武士共歌谣之，曰《兰陵王入阵曲》。今越调《兰陵王》，凡三段二十四拍，或曰遗声也。此曲声犯正宫，管色用大凡字，大一字、勾字，故一名《大犯》。”"}, {"id": 81, "name": "内家娇", "description": "《乐章集》注“林钟商”。"}, {"id": 82, "name": "冉冉云", "description": "韩淲词有“倚遍阑干弄花雨”句，更名《弄花雨》。"}, {"id": 83, "name": "凄凉犯", "description": "《白石词》注“仙吕调犯商调”，一名《瑞鹤仙影》。其自序曰：“合肥巷陌皆种柳，秋风夕起骚骚然。余客居阖户，时闻马嘶，出城四顾，则荒烟野草，不胜凄黯，乃著此解。琴有《凄凉调》，假以为名。凡曲言犯者，谓以宫犯商、商犯宫之类。如道调宫上字住，双调亦上字住，所住字同，故道调曲中犯双调，或于双调曲中犯道调，其他准此。唐人《乐书》云：犯有正、旁、偏、侧，宫犯宫为正，宫犯商为旁，宫犯角为偏，宫犯羽为侧。此说非也。十二宫所住字各不同，不容相犯。十二宫特可犯商、角、羽耳。”"}, {"id": 84, "name": "减字木兰花", "description": "《乐章集》注“仙吕调”。《梅苑》李子正词名《减兰》。徐介轩词名《木兰香》。《高丽史·乐志》名《天下乐令》。"}, {"id": 85, "name": "凤凰台上忆吹箫", "description": "《列仙传拾遗》云：“萧史善吹箫，作鸾凤之声。秦穆公有女弄玉，善吹箫，公以妻之，遂教弄玉作凤鸣。居十数年，凤凰来止，公为作凤台，夫妇止其上。数年，弄玉乘凤，萧史乘龙去。”调名取此。《高丽史·乐志》一名《忆吹箫》。"}, {"id": 86, "name": "凤凰阁", "description": "高拭词注“商调”。张炎词有“渐数花风第一”句，名《数花风》。"}, {"id": 87, "name": "凤孤飞", "description": "调见《小山乐府》。"}, {"id": 88, "name": "凤归云", "description": "唐教坊曲名。柳永《乐章集》平韵一百一字者注“仙吕调”。仄韵一百十八字者注“林钟商调”。"}, {"id": 89, "name": "凤来朝", "description": "调见周邦彦《清真词》。"}, {"id": 90, "name": "凤楼春", "description": "唐教坊曲名。"}, {"id": 91, "name": "凤池吟", "description": "调见《梦窗词》。"}, {"id": 92, "name": "凤衔杯", "description": "此调有平韵、仄韵两体。仄韵者，《乐章集》注“大石调”。"}, {"id": 93, "name": "凤鸾双舞", "description": "调见《水云词》。"}, {"id": 94, "name": "凭阑人", "description": "《太平乐府》注“越调”。按《唐书·礼乐志》：越调，即黄钟之商声也。"}, {"id": 95, "name": "击梧桐", "description": "此调有两体。一百八字者见《乐章集》，注“中吕调”。一百十字者见《乐府雅词》。"}, {"id": 96, "name": "别怨", "description": "调见《惜香乐府》。因词有“翻成别怨不胜悲”句，取以为名。"}, {"id": 97, "name": "剑器近", "description": "《宋史·乐志》：“教坊奏《剑器》曲，其一属中吕宫，其二属黄钟宫，又有《剑器》舞队。”此云“近”者，其声调相近也。"}, {"id": 98, "name": "剔银灯", "description": "《乐章集》注“仙吕调”。金词亦注“仙吕调”。元高拭词注“中吕宫”。蒋氏《九宫谱》属中吕调，名《剔银灯引》。"}, {"id": 99, "name": "剔银镫", "description": "《乐章集》注“仙吕调”。金词亦注“仙吕调”。元高拭词注“中吕宫”。蒋氏《九宫谱》属中吕调，名《剔银灯引》。"}, {"id": 100, "name": "剪牡丹", "description": "《宋史·乐志》：女弟子舞队，第四曰佳人剪牡丹队。调名本此。"}, {"id": 101, "name": "劝金船", "description": "张先词序：“流杯堂唱和翰林主人元素自撰腔。”苏轼词序：“和元素韵，自撰腔命名。”"}, {"id": 102, "name": "十二时慢", "description": "宋鼓吹四曲之一。《花草粹编》无“慢”字。此词有仄韵、平韵两体。"}, {"id": 103, "name": "十六字令", "description": "蔡伸词名《苍梧谣》，周玉晨词名《十六字令》，袁去华词亦名《归字谣》。有刻《归梧谣》者误。"}, {"id": 104, "name": "十月桃", "description": "调见《乐府雅词》，赋十月桃，即以为名。《梅苑》无名氏词，咏十月梅，即名《十月梅》。"}, {"id": 105, "name": "十样花", "description": "宋李弥逊词十首，分咏十样花，故名。"}, {"id": 106, "name": "千年调", "description": "曹组词名《相思会》，因词有“刚作千年调”句，辛弃疾改名《千年调》。"}, {"id": 107, "name": "千秋岁", "description": "《宋史·乐志》：“歇指调”。金词注“中吕调”。一名《千秋节》。"}, {"id": 108, "name": "千秋岁引", "description": "《高丽史·乐志》名《千秋岁令》。李冠词名《千秋万岁》。"}, {"id": 109, "name": "升平乐", "description": "《宋史·乐志》：“教坊都知李德昇，作《万岁昇平乐》曲。”周密《天基节乐次》：“乐奏夹钟宫，第三盏，笙起《昇平乐慢》。”"}, {"id": 110, "name": "华清引", "description": "词赋华清旧事，因以名调。"}, {"id": 111, "name": "华胥引", "description": "按《列子》：“黄帝昼寝，而梦游于华胥，既寤，怡然自得。又二十八年，天下大治，几若华胥国矣。”调名取此，词见《清真集》。"}, {"id": 112, "name": "卓牌子", "description": "此调有两体，五十六字者始自杨无咎，一名《卓牌子令》。九十七字者始自万俟咏，一名《卓牌子慢》。"}, {"id": 113, "name": "卓牌子近", "description": "宋人填词，有犯、有近、有促拍、有近拍。近者，其腔调微近也。此调见《袁宣卿集》，名《卓牌子近》，因字句与《卓牌子》不同，故另录于此。"}, {"id": 114, "name": "南乡一剪梅", "description": "每段上三句《南乡子》体，下二句《一剪梅》体。"}, {"id": 115, "name": "南乡子", "description": "唐教坊曲名。此词有单调、双调。单调者始自欧阳炯词，冯延巳、李珣俱本此添字。双调者始自冯延巳词。《太和正音谱》注“越调”。欧阳修本此减字，王之道、黄机、赵长卿俱本此添字也。"}, {"id": 116, "name": "南州春色", "description": "调见元陶豰《辍耕录》，因词中有“管取南州春色”句，取以为名。"}, {"id": 117, "name": "南歌子", "description": "唐教坊曲名。此词有单调、双调。单调者始自温庭筠词，因词有“恨春宵”句，名《春宵曲》。张泌词本此添字，因词有“高卷水晶帘额”句，名《水晶帘》，又有“惊破碧窗残梦”句，名《碧窗梦》。郑子聃有《我爱沂阳好》词十首，更名《十爱词》。双调者有平韵、仄韵两体。平韵者始自毛熙震词，周邦彦、杨无咎、僧挥五十四字体，无名氏五十三字体，俱本此添字。仄韵者始自《乐府雅词》，惟石孝友词最为谐婉。周邦彦词名《南柯子》，程垓词名《望秦川》，田不伐词有“帘风不动蝶交飞”句，名《风蝶令》。"}, {"id": 118, "name": "南浦", "description": "按唐《教坊记》有《南浦子》曲，宋词盖借旧曲名，另倚新声也。此调有仄韵、平韵两体，宋人多填仄韵词，其平韵惟鲁词一体。"}, {"id": 119, "name": "卜算子", "description": "元高拭词注“仙吕调”。苏轼词有“缺月挂疏桐”句，名《缺月挂疏桐》。秦湛词有“极目烟中百尺楼”句，名《百尺楼》。僧皎词有“目断楚天遥”句，名《楚天遥》。无名氏词有“蹙破眉峰碧”句，名《眉峰碧》。"}, {"id": 120, "name": "卜算子慢", "description": "《乐章集》注“歇指调”。"}, {"id": 121, "name": "占春芳", "description": "苏轼咏梨花制此调，取词中第三句为名。"}, {"id": 122, "name": "厅前柳", "description": "朱雍词名《亭前柳》，金词注“越调”。"}, {"id": 123, "name": "厌金杯", "description": "调见《东山乐府》，一名《献金杯》。"}, {"id": 124, "name": "双双燕", "description": "调见《梅溪集》，词咏双燕，即以为名。"}, {"id": 125, "name": "双声子", "description": "《乐章集》注“林钟商”。"}, {"id": 126, "name": "双头莲", "description": "此调一百三字者见周邦彦《片玉集》。一百字者见陆游《放翁集》。"}, {"id": 127, "name": "双头莲令", "description": "调见赵师侠《坦庵集》，咏信丰双莲，故制此词。"}, {"id": 128, "name": "双瑞莲", "description": "调见《虚斋乐府》，词咏并头莲，即以为名。"}, {"id": 129, "name": "双雁儿", "description": "一名《双燕子》，《中原音韵》入商调。按此调微近《醉红妆》，但《醉红妆》后段第三句不用韵，此则前后俱用韵也。"}, {"id": 130, "name": "双韵子", "description": "调见张先词集。按金元曲子有双声叠韵，调名疑出于此。"}, {"id": 131, "name": "双鸂鶒", "description": "调见朱敦儒《樵歌词》，因词有“一对双飞鸂鶒”句，故名。元高拭词注“正宫”。"}, {"id": 132, "name": "受恩深", "description": "一作《爱恩深》。《乐章集》注“大石调”。"}, {"id": 133, "name": "古香慢", "description": "吴文英自度曲，原注“夷则商，犯无射宫”。"}, {"id": 134, "name": "合欢带", "description": "《乐章集》注“林钟商”。"}, {"id": 135, "name": "吊严陵", "description": "调见《乐府雅词》，李甲作。因词有“严光钓址空遗迹”及“离觞弔古寓目”句，取以为名。又结句有“回首暮云千古碧”句，名《暮云碧》。"}, {"id": 136, "name": "后庭宴", "description": "《庚溪诗话》云：“宋宣和中，掘地得石刻唐词，调名《后庭宴》。”"}, {"id": 137, "name": "后庭花", "description": "唐教坊曲名。张先词名《玉树后庭花》。《碧鸡漫志》云：“《玉树后庭花》，陈后主造，其诗皆以配声律，遂取一句为曲名。讹蜀时，孙光宪、毛熙震、李珣有《后庭花曲》，皆赋后主故事，不著宫调，两段各四句，似令也。”"}, {"id": 138, "name": "后庭花破子", "description": "《太平乐府》注“仙吕调”。《唐书·礼乐志》：夷则羽，俗呼仙吕调。此金元小令，与唐词《后庭花》、宋词《玉树后庭花》异。所谓破子者，以其繁声入破也。"}, {"id": 139, "name": "向湖边", "description": "江纬自制曲，因词有“向湖边柳外”之句，取以为名。"}, {"id": 140, "name": "品令", "description": "王行词注“夷则商”。"}, {"id": 141, "name": "哨遍", "description": "《苏轼集》注“般涉调”。或作《稍遍》。"}, {"id": 142, "name": "唐多令", "description": "《太和正音谱》：越调，亦入高平调。一作《糖多令》。周密因刘过词有“二十年重过南楼”句，名《南楼令》。张翥词有“花下钿箜篌”句，名《箜篌曲》。"}, {"id": 143, "name": "啰唝曲", "description": "唐范摅《云溪友议》云：“金陵有啰唝楼，乃陈后主所建。《啰唝曲》，刘采春所唱，皆当代才子所作五六七言绝句。一名《望夫歌》，元稹诗所谓‘更有恼人肠断处，选词能唱望夫歌’也。”"}, {"id": 144, "name": "喜团圆", "description": "调见《小山乐府》。《花草粹编》无名氏词有“与箇团圆”句，更名《与团圆》。"}, {"id": 145, "name": "喜春来", "description": "《太平乐府》注“中吕宫”。《太和正音谱》注“正宫”。一名《阳春曲》。"}, {"id": 146, "name": "喜朝天", "description": "调见张先词集，送蔡襄还朝作。按唐教坊有《朝天曲》，《宋史·乐志》有越调《朝天乐》曲，此盖借旧曲名自翻新声也。"}, {"id": 147, "name": "喜迁莺", "description": "此调有小令、长调两体。小令起于唐人，《太和正音谱》注“黄钟宫”。因韦庄词有“鹤冲天”句，更名《鹤冲天》。和凝词有“飞上万年枝”句，名《万年枝》。冯延巳词有“拂面春风长好”句，名《春光好》。宋夏竦词名《喜迁莺令》。晏几道词名《燕归来》。李德载词有“残腊里、早梅芳”句，名《早梅芳》。长调起于宋人，《梅溪集》注“黄钟宫”。《白石集》注“太簇宫，俗名中管高宫”。江汉词一名《烘春桃李》。"}, {"id": 148, "name": "喜长新", "description": "唐教坊曲名。"}, {"id": 149, "name": "喝火令", "description": "调见《琴趣外篇》。"}, {"id": 150, "name": "四园竹", "description": "调见《片玉集》。"}, {"id": 151, "name": "四槛花", "description": "调见曹勛《松隐集》。"}, {"id": 152, "name": "四犯令", "description": "调见侯寘《孏窟词》，李处全词更名《四和香》，关注词又名《桂华明》。"}, {"id": 153, "name": "四犯剪梅花", "description": "调见《龙洲词》。前后段首句不押韵者名《四犯剪梅花》，押韵者名《轳辘金井》。卢祖皋词名《月城春》。又名《锦园春》，一名《三犯锦园春》。"}, {"id": 154, "name": "回波乐", "description": "唐刘肃《大唐新话》：景龙中，中宗尝游兴庆池，侍宴者递起鼓舞，并唱《回波词》。给事中李景伯亦起舞，歌词云云。"}, {"id": 155, "name": "国香", "description": "周密词名《国香慢》，自注“夷则商”。"}, {"id": 156, "name": "垂丝钓", "description": "《中原音韵》注“商角调”。《太平乐府》注“商调”。"}, {"id": 157, "name": "垂杨", "description": "调见陈允平《日湖渔唱》，本咏垂杨，即以为名。"}, {"id": 158, "name": "城头月", "description": "调见李昴英《文溪词》，和广帅马天骥韵，赠道士梁青霞作。此词盖马天骥所倡也，取词中起句为名。"}, {"id": 159, "name": "塞垣春", "description": "调见《片玉词》。"}, {"id": 160, "name": "塞姑", "description": "见《乐府诗集》。盖唐时边塞闺人之词也。"}, {"id": 161, "name": "塞孤", "description": "调见《乐章集》，原注“般涉调”。本名《塞孤》，《词律》编入《塞姑》词，后者误。"}, {"id": 162, "name": "塞翁吟", "description": "调见《清真乐府》，取《淮南子》塞上叟事为调名。"}, {"id": 163, "name": "声声慢", "description": "蒋氏《九宫谱》注“仙吕调”。晁补之词名《胜胜慢》。吴文英词有“人在小楼”句，名《人在楼上》。此调有平韵、仄韵两体，平韵者以晁补之、吴文英、王沂孙词为正体，仄韵者以高观国词为正体。"}, {"id": 164, "name": "夏云峰", "description": "《乐章集》注“歇指调”。"}, {"id": 165, "name": "夏日燕黉堂", "description": "调见《乐府雅词》。"}, {"id": 166, "name": "多丽", "description": "一名《鸭头绿》，周格非词名《陇头泉》。此调有平韵、仄韵两体。"}, {"id": 167, "name": "夜半乐", "description": "唐教坊曲名。柳永《乐章集》注“中吕调”，盖借旧曲名另倚新声也。《碧鸡漫志》：“《唐史》，明皇自潞州还京师，夜半举兵诛韦后，制《夜半乐》、《还京乐》二曲。今黄钟宫有《三台夜半乐》，中吕调，有慢、有近拍、有序。”"}, {"id": 168, "name": "夜合花", "description": "调见《琴趣外篇》。按夜合花，合欢树也，唐韦应物诗：“夜合花开香满庭”，调名取此。"}, {"id": 169, "name": "夜游宫", "description": "金词注“般涉调”。贺铸词有“江北江南新念别”句，更名《新念别》。"}, {"id": 170, "name": "夜行船", "description": "《太平乐府》、《中原音韵》、元高拭词俱注“双调”。黄公绍词名《明月棹孤舟》。《词律》以《夜行船》混入《雨中花》，今照《花草粹编》分列。"}, {"id": 171, "name": "夜飞鹊慢", "description": "调见《片玉词》，一名《夜飞鹊》。"}, {"id": 172, "name": "大圣乐", "description": "《宋史·乐志》：“道调宫”。此调有平韵、仄韵两体。平韵者见《顺斋乐府》，仄韵者见《薲洲渔笛谱》。"}, {"id": 173, "name": "大有", "description": "调见《片玉集》。"}, {"id": 174, "name": "大椿", "description": "调见《松隐集》，盖应制寿词也，取《庄子》“大椿”句为名。"}, {"id": 175, "name": "大酺", "description": "调见《清真乐府》。按唐教坊曲有《大酺乐》，《羯鼓录》亦有太簇商《大酺乐》。宋词盖借旧曲名，自制新声也。"}, {"id": 176, "name": "天下乐", "description": "唐教坊曲名。"}, {"id": 177, "name": "天仙子", "description": "唐教坊曲名。按段安节《乐府杂录》，《天仙子》本名《万斯年》，李德裕进，属龟兹部舞曲。因皇甫松词有“懊恼天仙应有以”句，取以为名。"}, {"id": 178, "name": "天净沙", "description": "《太平乐府》注“越调”。无名氏词有“塞上清秋早寒”句，又名《塞上秋》。"}, {"id": 179, "name": "天门谣", "description": "贺铸词，有“牛渚天门险”句，因取为调名。李之仪《姑溪词》注：贺方回登采石娥眉亭作也。"}, {"id": 180, "name": "天香", "description": "《法苑珠林》云：“天童子天香甚香。”调名本此。"}, {"id": 181, "name": "太常引", "description": "《太和正音谱》注“仙吕宫”。一名《太清引》。韩淲词有“小春时候腊前梅”句，名《腊前梅》。"}, {"id": 182, "name": "太平年", "description": "见《高丽史·乐志》。"}, {"id": 183, "name": "夺锦标", "description": "调见《古山词》。白朴词名《清溪怨》。"}, {"id": 184, "name": "女冠子", "description": "唐教坊曲名。小令始于温庭筠，长调始于柳永。《乐章集》“淡烟飘薄”词注“仙吕调”，“断烟残雨”词注“大石调”。元高拭词注“黄钟宫”。柳永词一名《女冠子慢》。"}, {"id": 185, "name": "好事近", "description": "张辑词有“谁谓百年心事，恰钓船横笛”句，名《钓船笛》。韩淲词有“吟到翠圆枝上”句，名《翠圆枝》。"}, {"id": 186, "name": "好女儿", "description": "此调有两体。四十五字者起于黄庭坚，因词有“懒系酥胸罗带，羞见绣鸳鸯”句，名《绣带儿》，《花草粹编》一作《绣带子》。六十二字者起于晏几道，与黄词迥别。"}, {"id": 187, "name": "好时光", "description": "词见《尊前集》，唐明皇制，取结句三字为调名。"}, {"id": 188, "name": "如梦令", "description": "宋苏轼词注：此曲本唐庄宗制，名《忆仙姿》，嫌其名不雅，故改为《如梦令》，盖因此词中有“如梦。如梦。”叠句也。周邦彦又因此词首句，改名《宴桃源》。沈会宗词有“不见。不见。”叠句，名《不见》。张辑词有“比著梅花谁瘦”句，名《比梅》。《梅苑》词名《古记》。《鸣鹤余音》词名《无梦令》。魏泰双调词名《如意令》。"}, {"id": 189, "name": "如鱼水", "description": "《乐章集》注“仙吕调”。"}, {"id": 190, "name": "婆罗门令", "description": "调见柳永《乐章集》，原注“夹钟商”。与《婆罗门引》不同。"}, {"id": 191, "name": "婆罗门引", "description": "《梅苑》词名《婆罗门》，段克己词名《望月婆罗门引》。按唐《教坊记》有《婆罗门》小曲，《宋史·乐志》有婆罗门舞队。《乐苑》曰：“《婆罗门》，商调曲也。开元中，西凉节度杨敬述进。”《理道要诀》云：“天宝十三载，改《婆罗门》为《霓裳羽衣》，属黄钟宫。”宋词调名疑出于此。"}, {"id": 192, "name": "子夜歌", "description": "调见凤林书院元词，与《菩萨蛮令》词别名《子夜歌》者不同。"}, {"id": 193, "name": "字字双", "description": "见《才鬼记》。因每句有叠字，故名《字字双》。"}, {"id": 194, "name": "孤馆深沉", "description": "调见宋黄大舆《梅苑》词。"}, {"id": 195, "name": "孤鸾", "description": "调见朱敦儒《太平樵唱》。"}, {"id": 196, "name": "安公子", "description": "唐教坊曲名。《碧鸡漫志》云：“据《理道要诀》，唐时《安公子》在太簇角。今已不传，其见于世者，中吕调有《安公子近》，般涉调有《安公子慢》。”按柳永“长川波潋滟”词自注“中吕调”，“远岸收残雨”词自注“般涉调”，但蒋氏《十三调》谱采柳永“长川波潋滟”词，又注“正宫“。"}, {"id": 197, "name": "安平乐慢", "description": "调见万俟咏《大声集》。"}, {"id": 198, "name": "定西番", "description": "唐教坊曲名。"}, {"id": 199, "name": "定风波", "description": "唐教坊曲名。李珣词名《定风流》，张先词名《定风波令》。"}, {"id": 200, "name": "定风波慢", "description": "此调有两体。一百字者，柳永词注“林钟商”，张翥词注“商角调”，有《梅苑》词可校。一百五字者，柳永词注“夹钟商”，无宋词可校。"}, {"id": 201, "name": "宜男草", "description": "调见范成大《石湖词》。"}, {"id": 202, "name": "宝鼎现", "description": "调见《顺庵乐府》。李弥逊词名《三段子》。陈合词名《宝鼎儿》。"}, {"id": 203, "name": "宣清", "description": "柳永《乐章集》注“林钟宫”。"}, {"id": 204, "name": "宴清都", "description": "调始《清真乐府》，程垓词名《四代好》。"}, {"id": 205, "name": "宴琼林", "description": "唐教坊曲名。《宋史·乐志》：“双调”。"}, {"id": 206, "name": "家山好", "description": "调见《湘山野录》。因词中有“水晶宫里家山好”句，取为调名。"}, {"id": 207, "name": "寰海清", "description": "《宋史·乐志》：“琵琶曲名，大石调。”"}, {"id": 208, "name": "寻梅", "description": "调见《乐府雅词》及《梅苑》，盖咏梅花也。因词中有“朝来寻见”句。取以为名。"}, {"id": 209, "name": "寻芳草", "description": "调见《稼轩词》，自注：一名《王孙信》。"}, {"id": 210, "name": "导引", "description": "按宋鼓吹四曲，悉用教坊新声，车驾出入奏导引，此调是也。《宋史·乐志》：正宫、道调宫、黄钟宫、大石调、黄钟羽调、正平调、仙吕调，凡七曲，或五十字，或加一叠一百字。《金史·乐志》：五十字者属无射宫。按无射宫俗呼黄钟宫。"}, {"id": 211, "name": "寿山曲", "description": "调见赵德麟《侯鲭录》，南唐冯延巳作。因词中有“圣寿南山永同”句，故名。"}, {"id": 212, "name": "寿延长破字令", "description": "调见《高丽史·乐志》。"}, {"id": 213, "name": "寿楼春", "description": "调见《梅溪集》，盖自度曲也。"}, {"id": 214, "name": "寿阳曲", "description": "《太平乐府》注“双调”。一名《落梅风》。"}, {"id": 215, "name": "尉迟杯", "description": "此调有平韵、仄韵两体。仄韵者见柳永《乐章集》，注“夹钟商”。平韵者见晁补之《琴趣外篇》。"}, {"id": 216, "name": "小圣乐", "description": "金元好问自度曲。《太平乐府》、《太和正音谱》俱注“双调”。蒋氏《九宫谱目》入小石调。因词中前结有“骤雨过，打遍新荷”句，更名《骤雨打新荷》。"}, {"id": 217, "name": "小重山", "description": "《宋史·乐志》：“双调”。李邴词名《小冲山》。姜夔词名《小重山令》。韩淲词有“点染烟浓柳色新”句，名《柳色新》。"}, {"id": 218, "name": "小镇西犯", "description": "唐教坊曲有《镇西子》，唐乐府亦有《镇西》七言绝句诗，此盖以旧曲名，另创新声也。《乐章集》有两调，七十一字者名《小镇西犯》，七十九字者名《小镇西》，或名《镇西》，俱注“仙吕调”。"}, {"id": 219, "name": "少年心", "description": "调见山谷词。有两体，一名《添字少年心》。"}, {"id": 220, "name": "少年游", "description": "调见《珠玉集》。因词有“长似少年时”句，取以为名。《乐章集》注“林钟商调”。韩淲词有“明窗玉蜡梅枝好”句，更名《玉蜡梅枝》。萨都刺词名《小阑干》。"}, {"id": 221, "name": "少年游慢", "description": "调见张先词。因词有“少年得意时节”句，取以为名，与《少年游令》不同。"}, {"id": 222, "name": "尾犯", "description": "调见《乐章集》。“夜雨滴空阶”词注“正宫”，“晴烟羃羃”词注“林钟商”。秦观词名《碧芙蓉》。"}, {"id": 223, "name": "山亭宴", "description": "调见张先词集，有美堂赠彦猷主人作，盖自度曲也。"}, {"id": 224, "name": "山亭柳", "description": "此调有平韵、仄韵两体，平韵者始自晏殊，仄韵者始自杜安世。"}, {"id": 225, "name": "山花子", "description": "唐教坊曲名，一名《南唐浣溪沙》。《梅苑》名《添字浣溪沙》，《乐府雅词》名《摊破浣溪沙》，《高丽史·乐志》名《感恩多令》。"}, {"id": 226, "name": "巫山一段云", "description": "唐教坊曲名。《乐章集》注“双调”。"}, {"id": 227, "name": "市桥柳", "description": "调见《齐东野语》。因第二句有“折尽市桥官柳”句，取以为名。"}, {"id": 228, "name": "师师令", "description": "杨慎《词品》：李师师，汴京名妓，张先为制新词，名《师师令》。"}, {"id": 229, "name": "帝台春", "description": "唐教坊曲名。《宋史·乐志》：琵琶曲有《帝台春》，属无射宫。"}, {"id": 230, "name": "平湖乐", "description": "《太平乐府》注“越调”。金词名《平湖乐》，取王恽词“人在平湖醉”句也。元词名《小桃红》，取无名氏词“宜插小桃红”句也。亦名《采莲词》，取《太平乐府》“采莲湖上采莲娇”句也。"}, {"id": 231, "name": "并蒂芙蓉", "description": "《能改斋漫录》：“政和癸巳，大晟乐成，蔡京以晁端礼荐，诏乘驿赴阙。端礼至都，会禁中嘉莲生，遂属词以进，名《并蒂芙蓉》。”"}, {"id": 232, "name": "庆千秋", "description": "调见《翰墨全书》。周密《天基圣节乐次》云：“第十盏，笛独吹高平调《庆千秋》。”"}, {"id": 233, "name": "庆宣和", "description": "元张可久《小山乐府》自注“双调”。按《唐书·礼乐志》，双调乃夹钟之商声也。"}, {"id": 234, "name": "庆春宫", "description": "一名《庆宫春》。此调有平韵、仄韵两体。平韵体始自北宋，有周邦彦诸词。仄韵体始自南宋，有王沂孙诸词。"}, {"id": 235, "name": "庆春时", "description": "调见《小山乐府》，凡二首，俱庆赏春时宴乐之词。"}, {"id": 236, "name": "庆春泽", "description": "此调有两体，六十六字者见张先词，九十八字者见《梅苑》词。"}, {"id": 237, "name": "庆清朝", "description": "一作《庆清朝慢》。"}, {"id": 238, "name": "庆金枝", "description": "《高丽史·乐志》名《庆金枝令》。"}, {"id": 239, "name": "应天长", "description": "此调有令词、慢词。令词始于韦庄，又有顾夐、毛文锡两体。宋毛幵词名《应天长令》。慢词始于柳永，《乐章集》注“林钟商调”。又有周邦彦一体，名《应天长慢》。"}, {"id": 240, "name": "应景乐", "description": "词见《花草粹编》。"}, {"id": 241, "name": "引驾行", "description": "此调有五十二字者，有一百字者，有一百二十五字者。五十二字词，即一百字词前段。一百二十五字词，亦就一百字词多五句也。晁补之一百字词名《长春》。柳永一百字词注“中吕调”，一百二十五字词注“仙吕调”。"}, {"id": 242, "name": "归去来", "description": "调见《乐章集》词二首。因词有“歌筵舞、且归去”、“休惆怅、好归去”句，取以为名。四十九字者自注“正平调”，五十二字者自注“中吕宫”。按《唐书·乐志》，仲吕羽为正平调，夹钟羽为中吕调，燕乐七羽之二也。"}, {"id": 243, "name": "归国谣", "description": "唐教坊曲名。元颜奎词名《归平谣》。"}, {"id": 244, "name": "归字谣", "description": "蔡伸词名《苍梧谣》，周玉晨词名《十六字令》，袁去华词亦名《归字谣》。有刻《归梧谣》者误。"}, {"id": 245, "name": "归朝欢", "description": "《乐章集》注“夹钟商”。辛弃疾词有“菖蒲自照清溪绿”句，名《菖蒲绿》。"}, {"id": 246, "name": "归田乐", "description": "黄庭坚词名《归田乐引》。"}, {"id": 247, "name": "归自谣", "description": "《乐府雅词》注“道调宫”。一名《风光子》，赵彦端词名《思佳客》。《词律》编入《归国谣》者误。"}, {"id": 248, "name": "彩云归", "description": "《宋史·乐志》：“仙吕调”。《乐章集》注“中吕调”。"}, {"id": 249, "name": "彩凤飞", "description": "一作《彩凤舞》。"}, {"id": 250, "name": "彩鸾归令", "description": "袁去华词，名《青山远》。"}, {"id": 251, "name": "征招", "description": "《宋史·乐志》：“政和间，诏以大晟雅乐施于燕飨，御殿按试，补征、角二调，播之教坊。”调名始此。"}, {"id": 252, "name": "征招调中腔", "description": "唐段安节《乐府杂录》云，征音有其声而无其字。宋《大晟乐府》始补《征招》调。凡曲有歌头，有中腔，此《征招》调之中腔也。"}, {"id": 253, "name": "征部乐", "description": "柳永《乐章集》注“夹钟商”。"}, {"id": 254, "name": "御带花", "description": "调见《六一居士词》。"}, {"id": 255, "name": "御街行", "description": "柳永《乐章集》注“夹钟宫”。《古今词话》无名氏词有“听孤雁声嘹唳”句，更名《孤雁儿》。"}, {"id": 256, "name": "忆东坡", "description": "调见《相山居士词》，盖忆东坡作也，即以题为调名。"}, {"id": 257, "name": "忆余杭", "description": "见《湘山野录》。潘阆自度曲，因忆西湖诸胜，故名《忆余杭》。《词律》编入《酒泉子》者误。"}, {"id": 258, "name": "忆少年", "description": "万俟咏词有“上陇首、凝眸天四阔”句，名《陇首山》。朱敦儒词名《十二时》。元刘秉忠词有“恨桃花流水”句，更名《桃花曲》。"}, {"id": 259, "name": "忆帝京", "description": "《乐章集》注“南吕调”。"}, {"id": 260, "name": "忆旧游", "description": "调始《清真乐府》，一名《忆旧游慢》。"}, {"id": 261, "name": "忆汉月", "description": "唐教坊曲名。柳永词名《望汉月》，《乐章集》注“正平调”。"}, {"id": 262, "name": "忆江南", "description": "宋王灼《碧鸡漫志》：此曲自唐至今，皆南吕宫，字句皆同，止是今曲两段，盖近世曲子无单遍者。"}, {"id": 263, "name": "忆王孙", "description": "此词单调三十一字者创自秦观，宋元人照此填。《太平乐府》注“黄钟宫”，《太和正音谱》注“仙吕宫”。《梅苑》词名《独脚令》，谢克家词名《忆君王》，吕渭老词名《豆叶黄》。陆游词有“画得蛾眉胜旧时”句，名《画蛾眉》。张辑词有“几曲阑干万里心”句，名《阑干万里心》。双调五十四字者见《复雅歌词》，或名《怨王孙》，与单调绝不同。坊刻又有仄韵单调《忆王孙》，查系《渔家傲》一段。故谱内不收。"}, {"id": 264, "name": "忆瑶姬", "description": "此调有仄韵、平韵两体。仄韵者始自曹组，一名《别素质》。平韵者始自万俟咏，一名《别瑶姬慢》。"}, {"id": 265, "name": "忆秦娥", "description": "元高拭词注“商调”。按此词昉自李白，自唐迄元，体各不一。要其源皆从李词出也。因词有“秦娥梦断秦楼月”句，故名《忆秦娥》，更名《秦楼月》。苏轼词有“清光偏照双荷叶”句，名《双荷叶》。无名氏词有“水天摇盪蓬莱阁”句，名《蓬莱阁》。至贺铸始易仄韵为平韵。张辑词有“碧云暮合”句，名《碧云深》。宋媛孙道绚词有“花深深”句，名《花深深》。"}, {"id": 266, "name": "忆闷令", "description": "调见《小山乐府》。"}, {"id": 267, "name": "忆黄梅", "description": "调见《梅苑》。"}, {"id": 268, "name": "快活年近拍", "description": "金词注“黄钟宫”。《太和正音谱》：“双调”。"}, {"id": 269, "name": "念奴娇", "description": "《碧鸡漫志》云大石调，又转入道调宫，又转入高宫大石调。姜夔词注“双调”。元高拭词注“大石调，又大吕调”。苏轼“赤壁怀古”词有“大江东去，一樽还酹江月”句，因名《大江东去》，又名《酹江月》，又名《赤壁词》，又名《酹月》。曾觌词名《壶中天慢》。戴复古词有“大江西上”句，名《大江西上曲》。姚述尧词有“太平无事，欢娱时节”句，名《太平欢》。韩淲词有“年年眉寿，坐对南枝”句，名《寿南枝》，又名《古梅曲》。姜夔词名《湘月》，自注“即《念奴娇》，鬲指声。”张辑词有“柳花淮甸春冷”句，名《淮甸春》。米友仁词名《白雪词》。张翥词名《百字令》，又名《百字谣》。丘长春词名《无俗念》。游文仲词名《千秋岁》。《翰墨全书》词名《庆长春》，又名《杏花天》。"}, {"id": 270, "name": "思帝乡", "description": "唐教坊曲名。"}, {"id": 271, "name": "思归乐", "description": "《乐章集》注“林钟商”。"}, {"id": 272, "name": "思越人", "description": "调见《花间集》。按孙光宪词“馆娃宫外春深”，又“魂消目断西子”，张泌词“越波堤下长桥”，俱咏西子事，故名《思越人》，与《鹧鸪天》词别名《思越人》者不同。"}, {"id": 273, "name": "思远人", "description": "调见《小山乐府》，因词有“千里念行客”句，取其意以为名。"}, {"id": 274, "name": "怨三三", "description": "调见李之仪《姑溪词》，取词中前段结句意为名。"}, {"id": 275, "name": "怨回纥", "description": "此调本五言律诗，见《尊前集》。皇甫词第一首云：“白首南朝女，悉听异域歌。收兵颉利国，饮马胡卢河。”结二句云：“雕窠城上宿，吹笛泪滂沱。”盖戍妇之怨词也。"}, {"id": 276, "name": "恋情深", "description": "唐教坊曲名。"}, {"id": 277, "name": "恋绣衾", "description": "韩淲词有“泪珠弹，犹带粉香”句，名《泪珠弹》。"}, {"id": 278, "name": "恋芳春慢", "description": "调见万俟咏《大声集》。崇宁中，咏充大晟府制撰，依月用律制词，多应制之作。此词自注“寒食前进，故以《恋芳春》为名也。”"}, {"id": 279, "name": "恋香衾", "description": "金词注“仙吕调”。"}, {"id": 280, "name": "恨春迟", "description": "调见张先词集。"}, {"id": 281, "name": "恨来迟", "description": "《梅苑》词名《恨欢迟》。"}, {"id": 282, "name": "情久长", "description": "调见《圣求词》。"}, {"id": 283, "name": "惜余欢", "description": "黄庭坚自度腔，因词有“少延欢洽”句，取以为名。"}, {"id": 284, "name": "惜分飞", "description": "贺铸词名《惜双双》，刘弇词名《惜双双令》，曹冠词名《惜芳菲》。"}, {"id": 285, "name": "惜奴娇", "description": "元高拭词注“双调”。按《高丽史·乐志》，宋赐大晟乐内有《惜奴娇曲破》，择其雅者，亦为类列。"}, {"id": 286, "name": "惜寒梅", "description": "调见《复雅歌词》，因词有“喜寒梅、却与雪期霜约”句，取以为名。"}, {"id": 287, "name": "惜春令", "description": "宋周密《天基圣节乐次》有“方响独打，正宫《惜春》”。"}, {"id": 288, "name": "惜春郎", "description": "调见《花草粹编》柳永词，因《乐章集》不载，故宫调无考。"}, {"id": 289, "name": "惜琼花", "description": "调见张先词集，为吴兴守时所赋也。"}, {"id": 290, "name": "惜秋华", "description": "吴文英自度曲。"}, {"id": 291, "name": "惜红衣", "description": "姜夔自度曲，属无射宫，取词内“红衣半狼籍”句为名。"}, {"id": 292, "name": "惜花春起早慢", "description": "调见《高丽史·乐志》，即赋题本意。"}, {"id": 293, "name": "惜黄花", "description": "调见《梅溪词》，金词注“仙吕调”。"}, {"id": 294, "name": "惜黄花慢", "description": "此调有仄韵、平韵两体。仄韵者见《逃禅词》，平韵者见《梦窗词》，与《惜黄花》令词不同。"}, {"id": 295, "name": "意难忘", "description": "元高拭词注“南吕调”。"}, {"id": 296, "name": "感恩多", "description": "唐教坊曲名。"}, {"id": 297, "name": "感皇恩", "description": "唐教坊曲名。陈旸《乐书》：祥符中，诸工请增龟兹部如教坊，其曲有《双调感皇恩》。金词注“大石调”。《中原音韵》注“南吕宫”。党怀英词名《叠萝花》。"}, {"id": 298, "name": "慢卷绸", "description": "柳永《乐章集》注“夹钟商”。"}, {"id": 299, "name": "戚氏", "description": "柳永《乐章集》注“中吕调”。丘处机词名《梦游仙》。"}, {"id": 300, "name": "扑蝴蝶", "description": "按周密《癸辛杂志》云：“吴有小妓，善舞《扑蝴蝶》。”疑是舞曲。邵叔齐词名《扑蝴蝶近》。"}, {"id": 301, "name": "扫地游", "description": "调见《清真词》，因词有“占地持杯，扫花寻路”句，取以为名。又名《扫花游》。"}, {"id": 302, "name": "扫地舞", "description": "唐教坊曲名。一名《扫市舞》。"}, {"id": 303, "name": "扬州慢", "description": "宋姜夔自度中吕宫曲。"}, {"id": 304, "name": "折丹桂", "description": "调见《相山词》。送人应举之作，取词中“仙籍桂香浮”句意为名，与《步蟾宫》别名《折丹桂》者不同。"}, {"id": 305, "name": "折桂令", "description": "《中原音韵》注“双调”。一名《秋风第一枝》，又名《天香引》，又名《蟾宫曲》。"}, {"id": 306, "name": "折红梅", "description": "调见《寿域词》。"}, {"id": 307, "name": "折花令", "description": "《调见高丽史·乐志》。"}, {"id": 308, "name": "抛毬乐", "description": "唐教坊曲名。《唐音癸笺》云：“《抛毬乐》，酒筵中抛毬为令，其所唱之词也。”"}, {"id": 309, "name": "拂霓裳", "description": "唐教坊曲名。《碧鸡漫志》：“《拂霓裳》，般涉调。”《宋史·乐志》：“女弟子舞队第五有拂霓裳队。”"}, {"id": 310, "name": "拜新月", "description": "唐教坊曲名。"}, {"id": 311, "name": "拜星月慢", "description": "一作《拜新月》。唐教坊曲名。《宋史·乐志》：“般涉调”。"}, {"id": 312, "name": "拨棹子", "description": "唐教坊曲名。"}, {"id": 313, "name": "拾翠羽", "description": "《洛神赋》：“或拾翠羽”，调名取此。"}, {"id": 314, "name": "换巢鸾凤", "description": "调见《梅溪词》，史达祖自制曲，因词中有“换巢鸾凤教偕老”句，取以为名。或云前段用平韵，后段叶仄韵，“换巢”之义，疑出于此。"}, {"id": 315, "name": "捣练子", "description": "《太和正音谱》注“双调”。一名《捣练子令》。因冯延巳词起结有“深院静”及“数声和月到帘栊”句，更名《深院月》。"}, {"id": 316, "name": "探春令", "description": "此调宋人俱咏初春风景，或咏梅花，故名《探春》。韩淲词有“景龙灯火昇平世”句，名《景龙灯》。"}, {"id": 317, "name": "探春慢", "description": "或作《探春》，无“慢”字。"}, {"id": 318, "name": "探芳信", "description": "调见《梅溪词》，张炎次周密“西泠春感”韵词，名《西湖春》。"}, {"id": 319, "name": "接贤宾", "description": "此调有两体，五十九字者始于毛文锡词，一百十七字者始于柳永词。《乐章集》注“林钟商调”。一名《集贤宾》。"}, {"id": 320, "name": "握金钗", "description": "《梅苑》无名氏词名《戛金钗》。"}, {"id": 321, "name": "摊破南乡子", "description": "《太平乐府》、《中原音韵》俱注“大石调”。高拭词注“南吕宫”。《太和正音谱》注“小石调，亦入仙吕宫”。赵长卿词名《青杏儿》，又名《似孃儿》。《翰墨全书》黄右曹词有“寿堂已庆灵椿老”句，名《庆灵椿》。《中州乐府》赵秉文词有“但教有酒身无事”句，名《閒閒令》。"}, {"id": 322, "name": "摊破采桑子", "description": "调见《惜香乐府》，即《采桑子令》也。因前后段俱添入和声，自成一体。"}, {"id": 323, "name": "摘得新", "description": "唐教坊曲名。"}, {"id": 324, "name": "摸鱼儿", "description": "一名《摸鱼子》，唐教坊曲名。晁补之词有“买陂塘、旋栽杨柳”句，更名《买陂塘》，又名《陂塘柳》，或名《迈陂塘》。辛弃疾赋怪石词名《山鬼谣》。李冶赋并蒂荷词有“请君试听双蕖怨”句，名《双蕖怨》。"}, {"id": 325, "name": "撼庭秋", "description": "唐教坊曲名。一作《感庭秋》。"}, {"id": 326, "name": "撼庭竹", "description": "此调有平韵、仄韵两体。"}, {"id": 327, "name": "散余霞", "description": "谢朓诗“余霞散成绮”，调名本此。"}, {"id": 328, "name": "散天花", "description": "唐教坊曲名。"}, {"id": 329, "name": "斗百草", "description": "调见《琴趣外篇》。"}, {"id": 330, "name": "新荷叶", "description": "蒋氏《九宫谱》作正宫引子。赵抃词名《折新荷引》，又因词中有“画桡稳，泛兰舟”句，或名《泛兰舟》，然与仄韵《泛兰舟》调迥别。"}, {"id": 331, "name": "新雁过妆楼", "description": "一名《雁过妆楼》，张炎词名《瑶台聚八仙》，陈允平词名《八宝妆》，《高丽史·乐志》名《百宝妆》。"}, {"id": 332, "name": "无愁可解", "description": "调见《东坡词》。自序云：“花日新作越调《解愁》，洛阳刘几伯寿闻而悦之，为作俚语诗，天下传咏，以为几欲达者。龙丘子笑之，此虽免乎愁，犹有所解也者，夫游于自然，而托于不得已，人乐亦乐，人愁亦愁，彼且乌乎解哉？乃反其词，作《无愁可解》。”"}, {"id": 333, "name": "无闷", "description": "调见《书舟词》，汲古阁本刻《闺怨无闷》者误。"}, {"id": 334, "name": "早梅芳", "description": "一名《早梅芳近》。"}, {"id": 335, "name": "早梅芳慢", "description": "调见柳永词，与《早梅芳近》不同。"}, {"id": 336, "name": "早梅香", "description": "调见《梅苑》，因词中有“探得早梅”及“乱飞香雪”句，故名。"}, {"id": 337, "name": "明月逐人来", "description": "按《能改斋漫录》云李持正自撰谱，盖因词有“皓月随人近远”句，故名。"}, {"id": 338, "name": "映山红慢", "description": "调见元载词，咏牡丹作。"}, {"id": 339, "name": "春云怨", "description": "调见冯艾子《云月词》，自注“黄钟商”。"}, {"id": 340, "name": "春从天上来", "description": "调见《中州乐府》，吴激词。"}, {"id": 341, "name": "春光好", "description": "唐教坊曲名。《碧鸡漫志·羯鼓录》云：“明皇尤爱羯鼓、玉笛，为八音之领袖。时春雨始晴，景色明丽，帝曰：‘对此岂可不为判断？’命取羯鼓，临轩纵击，曲名《春光好》。回顾柳杏，皆已微坼。上曰：‘此一事，不唤我作天工乎？’今夹钟宫《春光好》，唐以来多有此曲，或曰：夹钟宫属二月之律，明皇依月用律，故能判断如神。予曰，二月柳杏坼久矣，此必正月用二月律催之也。”按《羯鼓录》载，《春光好》曲入太簇宫，本正月律也，岂明皇所作乃太簇宫，而和凝等词入夹钟宫耶？今明皇词已不传，所传止《花间》、《尊前》集中词也。因晏几道词有“拼却一襟怀远泪，倚阑看”句，改名《愁倚阑令》，或名《愁倚阑》，或名《倚阑令》。"}, {"id": 342, "name": "春声碎", "description": "调见《翰墨全书》。取词前段结句三字为名。"}, {"id": 343, "name": "春夏两相期", "description": "调见《竹山词》。"}, {"id": 344, "name": "春晓曲", "description": "朱敦儒词有“西楼月落鸡声急”句，又名《西楼月》。"}, {"id": 345, "name": "春草碧", "description": "调见《大声集》，自注“中管高宫”。按《唐书·礼乐志》有中管之名，而不详其义。至宋仁宋《乐髓新经》始云：“大吕宫为高宫，太簇宫为中管高宫，盖以太簇宫与大吕宫同字谱，故谓之中管也，俗谱以中管高为调名者误。”"}, {"id": 346, "name": "春雪间早梅", "description": "调见《梅苑》词，櫽括韩愈《春雪间早梅》长律诗，即以题为调名。"}, {"id": 347, "name": "春风袅娜", "description": "调见《云月词》，冯艾子自度腔，注“黄钟羽”，即般涉调。"}, {"id": 348, "name": "昭君怨", "description": "朱敦儒词咏洛妃，名《洛妃怨》。侯寘词名《宴西园》。"}, {"id": 349, "name": "昼夜乐", "description": "《乐章集》注“中吕宫”。"}, {"id": 350, "name": "昼锦堂", "description": "此调有平韵、仄韵两体。平韵者见周邦彦《片玉集》，仄韵者见陈允平《日湖渔唱》。"}, {"id": 351, "name": "晴偏好", "description": "明陈耀文《花草粹编》云：“西湖虽有山泉，而大旱亦尝龟坼。嘉熙庚子水涸，茂草生焉。”李霜崖作《晴偏好》词纪之，取词中结句为调名。"}, {"id": 352, "name": "暗香", "description": "宋姜夔自度仙吕宫曲，咏梅花作也。张炎以此调咏荷花，更名《红情》。"}, {"id": 353, "name": "暗香疏影", "description": "张肎自度曲，以《暗香》调前段、《疏影》调后段合而为一。自注“夹钟宫”。"}, {"id": 354, "name": "曲江秋", "description": "韩玉词注“正宫”。"}, {"id": 355, "name": "曲游春", "description": "调见《薲洲渔笛谱》。"}, {"id": 356, "name": "曲玉管", "description": "唐教坊曲名。《乐章集》注“大石调”。"}, {"id": 357, "name": "更漏子", "description": "此调有两体，四十六字者始于温庭筠，唐宋词最多。《尊前集》注“大石调”，又属“商调”。一百四字者止杜安世词，无别首可录。"}, {"id": 358, "name": "最高楼", "description": "此调押平声韵，或押仄声韵，但宋金元词押平韵者居多，其中有前段起句三字、第三句五字者，有前段起句三字、第三句六字者，有前段起句四字、第三句六字者，例于后段第一、二句俱间押仄韵，此为定格。或后段第一、二句三声叶韵，或第一句押平韵、第二句不押韵，或第一句不押韵、第二句仍押平韵，或第一、二句俱不押韵，均属变格。若全押仄韵，则惟无名氏一词，见之《梅苑》，宋金元无填此体者。"}, {"id": 359, "name": "月上海棠", "description": "此调有两体，七十字者见《梅苑》无名氏词，金词注“双调”。陆游词有“几曾传玉关遥信”句，更名《玉关遥》。九十一字者，见姜夔《白石词》，注“夹钟商”。曹勛词名《月上海棠慢》。"}, {"id": 360, "name": "月下笛", "description": "调始周邦彦《片玉词》，因词有“凉蟾莹彻”及“静倚官桥吹笛”句，取以为名。"}, {"id": 361, "name": "月中桂", "description": "调见赵彦端词集。赵孟頫词平仄韵互押者，名《月中仙》。"}, {"id": 362, "name": "月华清", "description": "调见《空同词》。"}, {"id": 363, "name": "月宫春", "description": "调见《花间集》毛文锡词。周邦彦更名《月中行》。《宋史·乐志》属小石角。"}, {"id": 364, "name": "月当厅", "description": "调见《梅溪词》，史达祖自度曲也。"}, {"id": 365, "name": "月边娇", "description": "调见《薲洲渔笛谱》，周密自度曲。"}, {"id": 366, "name": "有有令", "description": "调见《惜香乐府》。"}, {"id": 367, "name": "望云涯引", "description": "调见《乐府雅词》。"}, {"id": 368, "name": "望云间", "description": "调见《翰墨全书》，赵可登代州南楼，自度此腔。"}, {"id": 369, "name": "望仙门", "description": "调见《珠玉词》，取词中结句为名。"}, {"id": 370, "name": "望南云慢", "description": "调见《乐府雅词》。"}, {"id": 371, "name": "望明河", "description": "调见《苕溪集》。"}, {"id": 372, "name": "望春回", "description": "调见《乐府雅词》。"}, {"id": 373, "name": "望梅花", "description": "唐教坊曲名。《梅苑》词作《望梅花令》。"}, {"id": 374, "name": "望江东", "description": "调见《山谷集》，因词有“望不见、江东路”句，取以为名。"}, {"id": 375, "name": "望江怨", "description": "调见花间集。"}, {"id": 376, "name": "望海潮", "description": "柳永《乐章集》注“仙吕调”。"}, {"id": 377, "name": "望湘人", "description": "调见《东山乐府》。"}, {"id": 378, "name": "望远行", "description": "唐教坊曲名。令词始自韦庄。《中原音韵》注“商调”。《太和正音谱》亦注“商调”。慢词始自柳永，“绣帏睡起”词注“中吕调”，“长空降瑞”词注“仙吕调”。"}, {"id": 379, "name": "朝中措", "description": "《宋史·乐志》：属黄钟宫。李祁词有“初见照江梅”句，名《照江梅》。韩淲词名《芙蓉曲》，又有“香动梅梢圆月”句，名《梅月圆》。"}, {"id": 380, "name": "朝天子", "description": "唐教坊曲名。《阳春集》名《思越人》。"}, {"id": 381, "name": "朝玉阶", "description": "见杜安世《寿域词》。其调近《散天花》，然换头句平仄自不同也。"}, {"id": 382, "name": "期夜月", "description": "《花草粹编》原注：“乐部中，惟杖鼓鲜有能工之者，京师官妓杨素娥最工，刘濬酷爱之，作《期夜月》词，素娥以此名动京师。”"}, {"id": 383, "name": "木兰花令", "description": "唐教坊曲名。《太和正音谱》注“高平调”。按《花间集》载《木兰花》、《玉楼春》两调，其七字八句者为《玉楼春》体，《木兰花》则韦词、毛词、魏词共三体，从无与《玉楼春》同者。自《尊前集》误刻以后，宋词相沿，率多混填。今照《花间集》本分列，旧谱误者悉为校正。"}, {"id": 384, "name": "木兰花慢", "description": "宋柳永《乐章集》注“高平调”。"}, {"id": 385, "name": "木笪", "description": "唐《教坊记》有《木笪》大曲，宋修内司所刊《乐府浑成集》亦有《木笪》曲名。周密《齐东野语》以为此音世人罕知。今《太平乐府》有白朴《乔木笪》词一套，疑其遗制。因《太和正音谱》采其首作，亦录以备一体。或名“乔木查”者误。"}, {"id": 386, "name": "杏园芳", "description": "调见《花间集》。"}, {"id": 387, "name": "杏花天", "description": "蒋氏《九宫谱目》入越调，辛弃疾词名《杏花风》。此调微近《端正好》，坊本颇多误刻。今以六字折腰者为《端正好》，六字一气者为《杏花天》。"}, {"id": 388, "name": "杏花天慢", "description": "调见《松隐集》。"}, {"id": 389, "name": "杜韦娘", "description": "唐《教坊记》有《杜韦娘》曲。刘禹锡诗“春风一曲杜韦娘”是也。宋人借旧曲名，另翻慢词。"}, {"id": 390, "name": "杨柳枝", "description": "唐教坊曲名。按白居易诗注：《杨柳枝》，洛下新声。其诗云：“听取新翻杨柳枝”是也。薛能诗序：令部妓作杨柳枝健舞，复度新声。其诗云：“试蹋吹声作唱声”是也。盖乐府横吹曲有《折杨柳》名，此则借旧曲名，另创新声，后遂入教坊耳。此本唐人七言绝句，与顾夐词四十字体、朱敦儒词四十四字体添声者不同。"}, {"id": 391, "name": "松梢月", "description": "调见曹勛《松隐集》，因词有“喜挹蟾华当松顶”句，取以为名。"}, {"id": 392, "name": "极相思", "description": "宋彭乘《墨客挥犀》云：“仁庙时，皇族中太尉夫人，一日入内，再拜告帝曰：‘妾有夫，不幸为婢妾所惑。’帝怒，流婢于千里。夫人亦得罪，居瑶华宫。太尉罚俸而不得朝。经岁，方春暮，夫人为词曲，名《极相思》，或加‘令’字。”"}, {"id": 393, "name": "枕屏儿", "description": "调见《梅苑》。"}, {"id": 394, "name": "柘枝引", "description": "唐教坊曲名。《乐府杂录》：“健舞曲”。《乐苑》：“羽调曲”。"}, {"id": 395, "name": "柳初新", "description": "宋周密《天基圣节乐次》：第十三盏，觱篥起《柳初新慢》。《乐章集》注“大石调“。"}, {"id": 396, "name": "柳含烟", "description": "唐教坊曲名。《花间集》毛文锡词有“河桥柳，占芳春，映水含烟拂露”句，取为调名。"}, {"id": 397, "name": "柳摇金", "description": "调见《梅苑》。"}, {"id": 398, "name": "柳梢青", "description": "此调两体，或押平韵，或押仄韵，字句悉同。押平韵者，宋韩淲词有“云淡秋空”句，名《云淡秋空》，有“雨洗元宵”句，名《雨洗元宵》，有“玉水明沙”句，名《玉水明沙》。元张雨词名《早春怨》。押仄韵者，《古今词话》无名氏词有“陇头残月”句，名《陇头月》。"}, {"id": 399, "name": "柳腰轻", "description": "调见《乐章集》，注“中吕宫“。因词有“英英妙舞腰肢软，章台柳，昭阳燕”句，取以为名。"}, {"id": 400, "name": "桂枝香", "description": "调见《乐府雅词》。张辑词有“疏帘淡月”句，又名《疏帘淡月》。"}, {"id": 401, "name": "桂殿秋", "description": "本唐李德裕《送神》、《迎神》曲，有“桂殿夜凉吹玉笙”句，取为调名。"}, {"id": 402, "name": "桃源忆故人", "description": "一名《虞美人影》。张先词或名《胡捣练》，陆游词名《桃源忆故人》，赵鼎词名《醉桃园》。韩淲词有“杏花风里东风峭”，名《杏花风》。"}, {"id": 403, "name": "梁州令", "description": "唐教坊曲名，一名《凉州令》。晁补之词名《梁州令叠韵》，盖合两首为一首也。《碧鸡漫志》云：“凉州即梁州，有七宫曲。”\t按柳永《乐章集》注“中吕宫”。"}, {"id": 404, "name": "梅子黄时雨", "description": "调见《山中白云词》。"}, {"id": 405, "name": "梅弄影", "description": "调见《丘崈集》咏梅词，因结句有“巡池看弄影”名，取以为名。"}, {"id": 406, "name": "梅花引", "description": "此调有两体，五十七字者，《中原音韵》注“越调”。高宪词有“须信在家贫也乐”句，名《贫也乐》。一百十四字者，即五十七字体再加一叠，贺铸词名《小梅花》。"}, {"id": 407, "name": "梅香慢", "description": "调见《东山乐府》。"}, {"id": 408, "name": "梦仙郎", "description": "调见张先词集。"}, {"id": 409, "name": "梦扬州", "description": "宋秦观自制曲，取词中结句为名。"}, {"id": 410, "name": "梦横塘", "description": "调见《苕溪词》。"}, {"id": 411, "name": "梦玉人引", "description": "此调有平韵、仄韵两体，字句大同小异。"}, {"id": 412, "name": "梦芙蓉", "description": "吴文英自度曲，题赵昌所画芙蓉作也，因词有“梦断琼仙”句，故名《梦芙蓉》。"}, {"id": 413, "name": "梦行云", "description": "调见《梦窗词藁》。自注：“一名《六幺花十八》。”《碧鸡漫志》云：“六幺曲内一叠，名《花十八》，前后十八拍。”"}, {"id": 414, "name": "梦还京", "description": "《乐章集》注“大石调”。"}, {"id": 415, "name": "梧叶儿", "description": "《太平乐府》注“商调”。《唐书·礼乐志》：商调，乃夷则之商声也。"}, {"id": 416, "name": "梧桐影", "description": "宋周紫芝《竹坡诗话》云：“大梁景德寺峨嵋院壁间，有吕岩题字。寺僧相传，有蜀僧号峨嵋道者，戒律甚严，不下席者二十年。一日，有布衣青裘，昂然一伟人来，与语良久，期以明年是日，复相见于此，愿少见待。明年是日，日方午，道者沐浴端坐而逝。至暮，伟人果来，问道者，曰亡矣。伟人叹息良久，忽不见。明日，书数语于堂侧壁间绝高处。宣和间，余游京师，犹及见之。”"}, {"id": 417, "name": "楚宫春慢", "description": "调见《宝月词》。"}, {"id": 418, "name": "楼上曲", "description": "调见《芦川词》。因词中有“楼外”、“楼中”二句，故名。"}, {"id": 419, "name": "檐前铁", "description": "调见《古今词话》。因词中有“檐前铁马戛叮噹”句，故名。"}, {"id": 420, "name": "欸乃曲", "description": "唐元结诗自序：大历初，结为道州刺史，以军事诣都。使还州，逢春水，舟行不进，作《欸乃曲》，令舟子唱之，以取适于道路云。"}, {"id": 421, "name": "歌头", "description": "《尊前集》注“大石调”。"}, {"id": 422, "name": "步月", "description": "此调有平韵、仄韵两体。平韵者见史达祖《梅溪词》，仄韵者见施岳《梅川词》。"}, {"id": 423, "name": "步虚子令", "description": "调见《高丽史·乐志》。"}, {"id": 424, "name": "步蟾宫", "description": "蒋氏《九宫谱目》入南吕引子。韩淲词名《钓台词》，刘拟词名《折丹桂》。"}, {"id": 425, "name": "武陵春", "description": "《梅苑》名《武林春》。"}, {"id": 426, "name": "殢人娇", "description": "《乐章集》注“林钟商”。"}, {"id": 427, "name": "殿前欢", "description": "《太平乐府》注“双调”。一名《凤将雏》。"}, {"id": 428, "name": "氐州第一", "description": "调始《清真乐府》，一名《熙州摘遍》。"}, {"id": 429, "name": "水仙子", "description": "唐教坊曲名。《太平乐府》注“双调”。"}, {"id": 430, "name": "水晶帘", "description": "调见《翰墨全书》。"}, {"id": 431, "name": "水调歌头", "description": "《碧鸡漫志》属中吕调。毛滂词名《元会曲》，张榘词名《凯歌》。按《水调》乃唐人大曲，凡大曲有歌头，此必裁截其歌头，另倚新声也。"}, {"id": 432, "name": "水龙吟", "description": "姜夔词注“无射商”，俗名越调。曾觌词结句有“是丰年瑞”句，名《丰年瑞》。吕渭老词名《鼓笛慢》。史达祖词名《龙吟曲》。杨樵云词因秦观词起句，更名《小楼连苑》。方味道词结句有“伴庄椿岁”句，名《庄椿岁》。"}, {"id": 433, "name": "永遇乐", "description": "周密《天基节乐次》：“乐奏夹钟宫，第五盏，觱篥起《永遇乐慢》。”此调有平韵、仄韵两体。仄韵者始自北宋，《乐章集》注“林钟商”。晁补之词名《消息》，自注“越调”。平韵者始自南宋，陈允平创为之。"}, {"id": 434, "name": "汉宫春", "description": "《高丽史·乐志》名《汉宫春慢》。此调有平韵、仄韵两体，平韵词八首，仄韵词两首，皆以前后段起句用韵、不用韵辨体。"}, {"id": 435, "name": "江亭怨", "description": "《花庵词选》名《清平乐令》。按《冷斋夜话》云：“黄鲁直登荆州亭，见亭柱间有此词，夜梦一女子云‘有感而作’，鲁直惊悟曰：‘此必吴城小龙女也。’因又名《荆州亭》。”"}, {"id": 436, "name": "江南春慢", "description": "吴文英自度曲，注“小石调”。"}, {"id": 437, "name": "江城子", "description": "唐词单调，以韦庄词为主，余俱照韦词添字。至宋人始作双调，晁补之改名《江神子》，韩淲词有“腊后春前邨意远”句，更名《邨意远》。"}, {"id": 438, "name": "江城子慢", "description": "调见吕渭老集。蔡松年词名《江神子慢》。与《江城子》令词不同。"}, {"id": 439, "name": "江城梅花引", "description": "按万俟咏《梅花引》句读与《江城子》相近，故可合为一调。程垓词换头句藏短韵者名《摊破江城子》。江皓词三声叶者四首，每首有一“笑”字，名《四笑江梅引》。周密词三声叶韵者名《梅花引》，全押平韵者名《明月引》。陈允平词名《西湖明月引》。"}, {"id": 440, "name": "江月晃重山", "description": "调见杨慎《词林万选》。每段上三句《西江月》体，下二句《小重山体》。"}, {"id": 441, "name": "沁园春", "description": "金词注“般涉调”。蒋氏《十三调》注“中吕调”。张辑词结句有“号我东仙”句，名《东仙》。李刘词名《寿星明》。秦观减字词名《洞庭春色》。"}, {"id": 442, "name": "沙塞子", "description": "唐教坊曲名。一名《沙碛子》。"}, {"id": 443, "name": "河传", "description": "宋王灼《碧鸡漫志》云，《河传》，唐曲，今存者二。其一属南吕宫，前段仄韵，后段平韵。其一属无射宫，即《怨王孙》曲，外又有越调、仙吕调两曲。"}, {"id": 444, "name": "河渎神", "description": "唐教坊曲名。《花庵词选》云：“唐词多缘题所赋，《河渎神》之咏祠庙，亦其一也。”"}, {"id": 445, "name": "河满子", "description": "唐教坊曲名。一名《何满子》。白居易诗注:开元中，沧州歌者姓名。元稹诗云：“便将何满为曲名，御府新题乐府纂”是也。又《卢氏杂说》：唐文宗命宫人沈翘翘舞《河满子》词。又属舞曲。"}, {"id": 446, "name": "法曲献仙音", "description": "陈旸《乐书》云：“法曲兴于唐，其声始出清商部，比正律差四律，有铙钹钟磬之音，《献仙音》其一也。”又云：“圣朝法曲乐器有琵琶、五絃筝、箜篌、笙、笛、觱篥、方响、拍板，其曲所存，不过道调《望瀛》、小石《献仙音》而已，其余皆不复见矣。”"}, {"id": 447, "name": "法驾导引", "description": "宋陈与义词序云：“世传顷年都下市肆中，有道人携乌衣椎髻女子，买斗酒独饮，女子歌词以侑，凡九阕，皆非人世语。或记之，以问一道士。道士惊曰：‘此赤城韩夫人所制水府蔡真人《法驾导引》也。’乌衣女子疑龙云。得其三而忘其六，拟作三阕。”"}, {"id": 448, "name": "泛兰舟", "description": "调见《梅苑》。与前《新荷叶》别名《泛兰舟》平韵词不同。"}, {"id": 449, "name": "泛清波摘遍", "description": "按《宋史·乐志》有林钟商《泛清波》大曲。沈括《笔谈》：“凡曲每解有数叠者，裁截用之，谓之摘遍。”此盖摘《泛清波》曲之一遍也。"}, {"id": 450, "name": "泛青苕", "description": "调见张先词，吴兴泛舟作，即赋题本意也。一名《感皇恩慢》。"}, {"id": 451, "name": "洞仙歌", "description": "唐教坊曲名。此调有令词，有慢词。令词自八十三字至九十三字，共三十五首。康与之词名《洞仙歌令》，潘牥词名《羽仙歌》，袁易词名《洞仙词》。《宋史·乐志》名《洞中仙》，注“林钟商调”，又“歇指调”。金词注“大石调”。慢词自一百十八字至一百二十六字，共五首。柳永《乐章集》“嘉景”词注“般涉调”，“乘兴閒泛兰舟”词注“仙吕调”，“佳景留心惯”词注“中吕调”。按张綖《诗余图谱》，前段六句三韵，后段七句三韵，前后段第三句俱七字，第四句俱九字，前段结句六字，后段结句九字，此令词正体也，间有摊破、添字句、添韵者，皆从此出，谱中句读悉据之。"}, {"id": 452, "name": "洞天春", "description": "调见《六一词》，盖赋院落之春景如洞天也。"}, {"id": 453, "name": "浣溪沙", "description": "唐教坊曲名。张泌词有“露浓香泛小庭花”句，名《小庭花》。贺铸名《减字浣溪沙》。韩淲词有“芍药酴醾满院春”句，名《满院春》。有“东风拂栏露犹寒”句，名《东风寒》。有“一曲西风醉木犀”句，名《醉木犀》。有“霜后黄花菊自开”句，名《霜菊黄》。有“广寒曾折最高枝”句，名《广寒枝》。有“春风初试薄罗衫”句，名《试香罗》。有“清和风里绿荫初”句，名《清和风》。有“一番春事怨啼鹃”句，名《怨啼鹃》。"}, {"id": 454, "name": "浣溪沙慢", "description": "调见《片玉集》，亦名《浣溪纱慢》。"}, {"id": 455, "name": "浪淘沙", "description": "唐教坊曲名。"}, {"id": 456, "name": "浪淘沙令", "description": "《乐章集》注“歇指调”。蒋氏《九宫谱目》“越调”。按《唐书·礼乐志》，歇指调乃林钟律之商声。越调乃无射律之商声也。贺铸词名《曲入冥》，李清照词名《卖花声》，史达祖词名《过龙门》，马钰词名《炼丹砂》。"}, {"id": 457, "name": "浪淘沙慢", "description": "柳永《乐章集》注“歇指调”。"}, {"id": 458, "name": "海棠春", "description": "此调始自秦观，因词中有“试问海棠花，昨夜开多少”句，故名。马庄父词名《海棠花》，史达祖词名《海棠春令》。"}, {"id": 459, "name": "淡黄柳", "description": "姜夔自度曲。《白石集》注“正平调”。"}, {"id": 460, "name": "添声杨柳枝", "description": "按《碧鸡漫志》云：“黄钟商有《杨柳枝》曲，仍是七言四句诗，与刘、白及五代诸子所制并同，但每句下各添三字一句，乃唐时和声，如《竹枝》、《渔父》，今皆有和声也。旧词多侧字起头，第三句亦复侧字起，声度差稳耳。”今名《添声杨柳枝》，欧阳修词名《贺圣朝影》，贺铸词名《太平时》。《宋史·乐志》：《太平时》，小石调。"}, {"id": 461, "name": "清商怨", "description": "古乐府有清商曲辞，其音多哀怨，故取以为名。周邦彦以晏词有“关河愁思”句，更名《关河令》，又名《伤情怨》。"}, {"id": 462, "name": "清平乐", "description": "《宋史·乐志》：“属大石调”。《乐章集》注“越调”。《碧鸡漫志》云：“欧阳炯称李白有应制《清平乐》四首，此其一也，在越调，又有黄钟宫、黄钟商两音。”《花庵词选》名《清平乐令》。张辑词有“忆著故山萝月”句，名《忆萝月》。张翥词有“明朝来醉东风”句，名《醉东风》。"}, {"id": 463, "name": "清江曲", "description": "此宋苏庠泛舟清江作也，体近古诗。因《花草粹编》采入，今仍之。"}, {"id": 464, "name": "清波引", "description": "调见《白石集》，姜夔自度曲。"}, {"id": 465, "name": "清风八咏楼", "description": "沈隐侯守东阳，建八咏楼，其地又有双溪之胜，故曰“明月双溪水，清风八咏楼”，调名取此。王行词注“林钟商曲”。《清风八咏楼》者，南宋词林所制也。"}, {"id": 466, "name": "清风满桂楼", "description": "调见《松隐集》。"}, {"id": 467, "name": "渔家傲", "description": "明蒋氏《九宫谱目》入中吕引子。"}, {"id": 468, "name": "渔歌子", "description": "唐教坊曲名。按《唐书·张志和传》，志和居江湖，自称江波钓徒，每垂钓不设铒，志不在鱼也。宪宗图真，求其人不能致。尝撰《渔歌》，即此词也。单调体实始于此。至双调体，昉自《花间集》顾夐、孙光宪。有魏承班、李珣诸词可校。若苏轼单调词，则又从双调词脱化耳。和凝词更名《渔父》，徐积词名《渔父乐》。"}, {"id": 469, "name": "渔父引", "description": "唐教坊曲名。"}, {"id": 470, "name": "渡江云", "description": "周密词名《三犯渡江云》。此调后段第四句例用仄韵，亦是三声叶，乃一定之格。宋元人俱如此填，惟陈允平有全押平韵、全押仄韵二体。"}, {"id": 471, "name": "湘春夜月", "description": "黄孝迈自度曲。"}, {"id": 472, "name": "湘江静", "description": "调见《乐府雅词》，一名《潇湘静》。"}, {"id": 473, "name": "满宫花", "description": "调见《花间集》。尹鹗赋宫怨词有“满地禁花慵扫”句，取以为名。"}, {"id": 474, "name": "满庭芳", "description": "此调有平韵、仄韵两体。平韵者，周邦彦词名《锁阳台》。葛立方词有“要看黄昏庭院，横斜映霜月朦胧”句，名《满庭霜》。晁补之词有“堪与潇湘暮雨，图上画扁舟”句，名《潇湘夜雨》。韩淲词有“甘棠遗爱，留与话桐乡”句，名《话桐乡》。吴文英词因苏轼词有“江南好，千钟美酒，一曲满庭芳”句，名《江南好》。张埜词名《满庭花》。《太平乐府》注“中吕宫”，高拭词注“中吕调”。仄韵者，《乐府雅词》名《转调满庭芳》。"}, {"id": 475, "name": "满朝欢", "description": "《乐章集》注“大石调”。"}, {"id": 476, "name": "满江红", "description": "此调有仄韵、平韵两体，仄韵词，宋人填者最多，其体不一，今以柳词为正体，其余各以类列。《乐章集》注“仙吕调”，高栻词注“南吕调”。平韵词祇有姜词一体，宋元人俱如此填。"}, {"id": 477, "name": "滴滴金", "description": "蒋氏《九宫谱目》入黄钟宫。"}, {"id": 478, "name": "潇湘神", "description": "调始自唐刘禹锡咏湘妃词。所谓赋题本意也。"}, {"id": 479, "name": "潇湘逢故人慢", "description": "调见《花庵词选》。"}, {"id": 480, "name": "澡兰香", "description": "调见吴文英《梦窗甲稿》，因词有“午镜澡兰帘幕”句，取以为名。"}, {"id": 481, "name": "点绛唇", "description": "元《太平乐府》注“仙吕宫”。高拭词注“黄钟宫”。《正音谱》注“仙吕调”。宋王禹偁词名《点樱桃》。王十朋词名《十八香》。张辑词有“邀月过南浦”句，名《南浦月》。又有“遥隔沙头雨”句，名《沙头雨》。韩淲词有“更约寻瑶草”句，名《寻瑶草》。"}, {"id": 482, "name": "烛影摇红", "description": "宋吴曾《能改斋漫录》：王都尉诜有《忆故人》词，徽宗喜其词意，犹以不丰容宛转为恨，乃令大晟乐府别撰腔，周邦彦增益其词，而以首句为名，谓之《烛影摇红》。"}, {"id": 483, "name": "熙州慢", "description": "《唐书·礼乐志》：“天宝乐曲皆以边地名，若伊州、甘州、凉州之类。”\t按宋改镇洮军为熙州，本秦汉时陇西郡，亦边地也。调名《熙州》，义或取此。"}, {"id": 484, "name": "燕山亭", "description": "“燕”或作“宴”，然与《山亭宴》无涉。"}, {"id": 485, "name": "燕归梁", "description": "调见《珠玉词》，因词有“双燕归飞绕画堂，似留恋虹梁”句，取以为名。柳永“织锦裁篇”词注“正平调”。“轻嗫罗鞋”词注“中吕调”。"}, {"id": 486, "name": "燕春台", "description": "此调始自张先，盖春宴词也。因黄裳有夏宴词，刘泾改名《夏初临》，旧谱或以《燕春台》与《夏初临》两列者误。"}, {"id": 487, "name": "爪茉莉", "description": "调见《花草粹编》，《乐章集》不载。"}, {"id": 488, "name": "爱月夜眠迟慢", "description": "调见《高丽史·乐志》。宋词也，即赋本意。"}, {"id": 489, "name": "献天寿", "description": "见《高丽史·乐志》。"}, {"id": 490, "name": "献天寿令", "description": "调见《高丽史·乐志》。"}, {"id": 491, "name": "献衷心", "description": "唐教坊曲名。"}, {"id": 492, "name": "玉京秋", "description": "调见《薲洲渔笛谱》。"}, {"id": 493, "name": "玉京谣", "description": "吴文英自度曲，自注“夷则商，犯无射宫”。按《枕中书》：“玉京在大罗天之上。”李白诗有“手把芙蓉朝玉京”句。此文英赠陈藏一词，见《随隐漫录》，盖赋京华羁旅之况，故借玉京以为调名。"}, {"id": 494, "name": "玉人歌", "description": "调见《西樵语丛》。"}, {"id": 495, "name": "玉团儿", "description": "调见周邦彦《片玉词》。因《清真集》不载，故方千里、杨泽民、陈允平俱无和词，宋惟卢炳、袁去华两词可校。"}, {"id": 496, "name": "玉堂春", "description": "调见《珠玉集》。"}, {"id": 497, "name": "玉女摇仙佩", "description": "柳永《乐章集》注“正宫”。"}, {"id": 498, "name": "玉女迎春慢", "description": "调见凤林书院元词。"}, {"id": 499, "name": "玉山枕", "description": "柳永《乐章集》注“仙吕调”。"}, {"id": 500, "name": "玉抱肚", "description": "调见《逃禅词》。"}, {"id": 501, "name": "玉梅令", "description": "姜夔自度高平调曲，因词中有“玉梅几树”句，取以为名。"}, {"id": 502, "name": "玉梅香慢", "description": "调见《梅苑》，与《梅香慢》、《早梅香》、《雪梅香》不同。"}, {"id": 503, "name": "玉楼人", "description": "调见《梅苑》词选本。"}, {"id": 504, "name": "玉楼春", "description": "《花间集》顾夐词起句有“月照玉楼春漏促”句，又有“柳映玉楼春日晚”句，《尊前集》欧阳炯词起句有“春早玉楼烟雨夜”句，又有“日照玉楼花似锦，楼上醉和春色寝”句，取为调名。李煜词名《惜春容》，朱希真词名《西湖曲》，康与之词名《玉楼春令》，《高丽史·乐志》词名《归朝欢令》。《尊前集》注“大石调”，又“双调”。《乐章集》注“大石调”，又“林钟商调”。皆李煜词体也。《乐章集》又有仙吕调词，与各家平仄不同。"}, {"id": 505, "name": "玉漏迟", "description": "蒋氏《九宫谱》：黄钟宫。"}, {"id": 506, "name": "玉烛新", "description": "调始《清真乐府》。《尔雅》云：“四时和，谓之玉烛。”取以为名。"}, {"id": 507, "name": "玉簟凉", "description": "调见《梅溪词》。"}, {"id": 508, "name": "玉蝴蝶", "description": "小令始于温庭筠，长调始于柳永。《乐章集》注“仙吕调”。一名《玉蝴蝶慢》。"}, {"id": 509, "name": "玉连环", "description": "调见《云月词》。与《一落索》别名《玉连环》不同。"}, {"id": 510, "name": "玉阑干", "description": "调见《寿域词》。"}, {"id": 511, "name": "玲珑四犯", "description": "此调创自周邦彦《清真集》，方千里、杨泽民、陈允平俱有和词。姜夔又有自度黄钟商曲，与周词句读迥别，因调名同，故亦类列。"}, {"id": 512, "name": "玲珑玉", "description": "调见凤林书院元词，姚云文自度曲。"}, {"id": 513, "name": "珍珠令", "description": "调见张炎《山中白云词》。"}, {"id": 514, "name": "珠帘卷", "description": "调见欧阳修词，因词有“珠帘卷”句，取以为名。"}, {"id": 515, "name": "琐寒窗", "description": "一名《锁寒窗》，调见《片玉集》，盖寒食词也。因词有“静锁一庭愁雨”及“故人剪烛西窗雨”句，取以为名。"}, {"id": 516, "name": "琐窗寒", "description": "一名《锁寒窗》，调见《片玉集》，盖寒食词也。因词有“静锁一庭愁雨”及“故人剪烛西窗雨”句，取以为名。"}, {"id": 517, "name": "琵琶仙", "description": "姜夔自度黄钟商曲。"}, {"id": 518, "name": "瑞云浓", "description": "调见《逃禅集》。蒋氏《九宫谱》入黄钟宫。"}, {"id": 519, "name": "瑞云浓慢", "description": "按杨无咎《逃禅集》有七十五字《瑞云浓》，与此不同。"}, {"id": 520, "name": "瑞鹤仙", "description": "元高拭词注“正宫”。《夷坚志》云：“乾道中，吴兴周权知衢州西安县。一日，令术士沈延年邀紫姑神，赋《鹤鹤仙》牡丹词，有‘覩娇红一捻’句，因名《一捻红》。”"}, {"id": 521, "name": "瑞鹧鸪", "description": "《宋史·乐志》中吕调。元高拭词注“仙吕调”。《苕溪词话》云：“唐初歌词，多五言诗，或七言诗，今存者止《瑞鹧鸪》七言八句诗，犹依字易歌也。”"}, {"id": 522, "name": "瑞龙吟", "description": "黄昇云：“此调前两段双拽头，属正平调，后一段犯大石调，‘归骑晚’以下仍属正平调也。”"}, {"id": 523, "name": "瑶华", "description": "调见《梦窗词》，一名《瑶华慢》。"}, {"id": 524, "name": "瑶台月", "description": "调见《梅苑》。《鸣鹤余香》无名氏词名《瑶池月》。"}, {"id": 525, "name": "瑶台第一层", "description": "宋陈师道《后山诗话》：“武才人出庆寿宫，裕陵得之。会教坊献新声，为作词，号《瑶台第一层》。”"}, {"id": 526, "name": "瑶阶草", "description": "调见《书舟词》。"}, {"id": 527, "name": "甘州令", "description": "《碧鸡漫志》：仙吕调有《甘州令》。《乐章集·甘州令》注“亦仙吕调”。字句与《甘州子》、《甘州遍》、《八声甘州》不同。"}, {"id": 528, "name": "甘州曲", "description": "唐教坊曲名。《唐书·礼乐志》：天宝间乐曲，皆以边地为名，甘州其一也。顾夐词名《甘州子》。"}, {"id": 529, "name": "甘州遍", "description": "按唐教坊大曲有《甘州》。凡大曲多遍，此则《甘州曲》之一遍也。"}, {"id": 530, "name": "甘草子", "description": "《乐章集》注“正宫”。"}, {"id": 531, "name": "甘露歌", "description": "调见《乐府雅词》，一名《古祝英台》。"}, {"id": 532, "name": "甘露滴乔松", "description": "调见《翰墨全书》。"}, {"id": 533, "name": "生查子", "description": "唐教坊曲名。《尊前集》注“双调”。元高拭词注“南吕宫”。朱希真词有“遥望楚云深”句，名《楚云深》。韩淲词有“山意入春晴，都是梅和柳”句，名《梅和柳》。又有《晴色入青山》句，名《晴色入青山》。"}, {"id": 534, "name": "画堂春", "description": "调见《淮海集》。即咏画堂春色，取以为名。"}, {"id": 535, "name": "留客住", "description": "唐教坊曲名。《乐章集》注“林钟商”。"}, {"id": 536, "name": "留春令", "description": "调见《小山乐府》。"}, {"id": 537, "name": "番枪子", "description": "调见金韩玉《东浦词》。李献能因此词后段结句有“春草碧”句，更名《春草碧》。"}, {"id": 538, "name": "疏影", "description": "姜夔自度仙吕宫曲。张炎词咏荷叶，易名《绿意》。彭元逊词有“遗佩环浮沈澧浦”句，名《解佩环》。"}, {"id": 539, "name": "白苎", "description": "按《古乐府》有《白苎》曲，宋人盖借旧曲名别倚新声也。王灼《颐堂集》云：“《白苎》词，传者至少，其正宫一阕，世以为紫姑神作。”今从《花草粹编》，为柳永词。"}, {"id": 540, "name": "白雪", "description": "调见《逃禅集》，杨无咎自制曲。题本赋雪，故即以《白雪》名调。"}, {"id": 541, "name": "百媚孃", "description": "调见张先词集，取词中“百媚算应天乞与”句为名。"}, {"id": 542, "name": "百宜娇", "description": "调见《圣求词》，与《眉妩》词别名《百宜娇》者不同。"}, {"id": 543, "name": "皂罗特髻", "description": "调见宋苏轼词，词中有“髻鬟初合”句，亦赋题也。"}, {"id": 544, "name": "盐角儿", "description": "《碧鸡漫志》云：“始教坊家人市盐，于纸角中，得一曲谱，翻之，遂以为名，今双调《盐角儿令》是也。”"}, {"id": 545, "name": "相思儿令", "description": "《花草粹编》名《相思令》。"}, {"id": 546, "name": "相思引", "description": "此调有两体，四十六字者押平声韵，房舜卿词名《玉交枝》，周紫芝词名《定风波令》，赵彦端词名《琴调相思引》。四十九字者押仄声韵，《古今词话》无名氏词名《镜中人》。"}, {"id": 547, "name": "相见欢", "description": "唐教坊曲名。南唐李煜词有“无言独上西楼，月如鈎”句，更名《秋夜月》，又名《上西楼》，又名《西楼子》。康与之词名《忆真妃》。张辑词有“唯有渔竿明月上瓜州”句，因名《月上瓜州》。或名《乌夜啼》。"}, {"id": 548, "name": "眉妩", "description": "姜夔词注：“一名《百宜娇》。”"}, {"id": 549, "name": "看花回", "description": "琴曲有《看花回》，调名本此。此调有两体，六十八字者始自柳永，《乐章集》注“大石调”，《中原音韵》注“越调”，无别首宋词可校。一百一字者始自黄庭坚，有周邦彦、蔡伸、赵彦端诸词可校。"}, {"id": 550, "name": "真珠帘", "description": "调见《放翁词》。"}, {"id": 551, "name": "真珠髻", "description": "调见《梅苑》词。"}, {"id": 552, "name": "眼儿媚", "description": "左誉词有“斜月小阑干”句，名《小阑干》。韩淲词有“东风拂槛露犹寒”句，名《东风寒》。陆游词名《秋波媚》。"}, {"id": 553, "name": "睿恩新", "description": "调见《珠玉词》。此调近《金莲绕凤楼》，但前后段第三句亦用上三下四句法，不押韵，与《金莲绕凤楼》词全属七言诗句押韵者不同。"}, {"id": 554, "name": "石州慢", "description": "《宋史·乐志》：“越调”。贺铸词有“长亭柳色纔黄”句，名《柳色黄》。谢懋词名《石州引》。"}, {"id": 555, "name": "石湖仙", "description": "姜夔自度曲，寿范成大作也。成大号石湖，故以“石湖仙”命调，《白石集》注“越调”。"}, {"id": 556, "name": "破字令", "description": "调见《高丽史·乐志》。"}, {"id": 557, "name": "破阵乐", "description": "唐教坊曲名。《宋史·乐志》：“正宫”。柳永《乐章集》注“林钟商”。"}, {"id": 558, "name": "破阵子", "description": "唐教坊曲名，一名《十拍子》。陈旸《乐书》云：“唐《破阵子乐》属龟兹部，秦王所制，舞用二千人，皆画衣甲，执旗旆。外藩镇春衣犒军设乐，亦舞此曲，兼马军引入场，尤壮观也。”"}, {"id": 559, "name": "碧牡丹", "description": "金词注“中吕调”。"}, {"id": 560, "name": "祝英台近", "description": "元高拭词注“越调”。辛弃疾词有“宝钗分，桃叶渡”句，名《宝钗分》。张辑词有“趁月底重箫谱”句，名《月底修箫谱》。韩淲词有“燕莺语，溪岸点点飞锦”句，名《燕莺语》，又有“却又在他乡寒食”句，名《寒食词》。"}, {"id": 561, "name": "祭天神", "description": "调见柳永《乐章集》，八十四字词注“中吕调”，八十五字词注“歇指调”。"}, {"id": 562, "name": "福寿千春", "description": "调见《花草粹编》。"}, {"id": 563, "name": "离亭宴", "description": "调始张先，因词中有“随处是离亭别宴”句，取以为名。"}, {"id": 564, "name": "离别难", "description": "唐教坊曲名。按段安节《乐府杂录》：“天后朝，有士人妻配入掖庭，善吹觱篥，乃撰此曲，盖五言八句诗也。”《白居易集》亦有七言绝句诗。薛词见《花间集》，乃借旧曲名另倚新声者，因词有“罗帏乍别情难”句，取以为名。宋柳永词则又与薛词不同，《乐章集》注“中吕调”。"}, {"id": 565, "name": "秋兰香", "description": "调见《全芳备祖》。"}, {"id": 566, "name": "秋夜月", "description": "调见《尊前集》，因尹鹗词起结有“三秋佳节”及“夜深、窗透数条寒月”句，取以为名。《乐章集》注“夹钟商”。"}, {"id": 567, "name": "秋夜雨", "description": "调见蒋捷《竹山乐府》，题“咏秋雨”。"}, {"id": 568, "name": "秋宵吟", "description": "宋姜夔自度越调曲。"}, {"id": 569, "name": "秋思耗", "description": "调见《梦窗词》，吴文英自度腔。因词有“偏称画屏秋色”句，更名《画屏秋色》。"}, {"id": 570, "name": "秋色横空", "description": "调见《天籁集》。"}, {"id": 571, "name": "秋蕊香", "description": "此调有两体，四十八字者始于晏殊，九十七字者始于赵以夫，两词迥别，因词名同，故为类列。若柳永六十字《秋蕊香引》，仍即挨字另编。"}, {"id": 572, "name": "秋蕊香引", "description": "《乐章集》注“小石调”。"}, {"id": 573, "name": "秋霁", "description": "一名《春霁》。按此调始自胡浩然，赋春晴词即名《春霁》。赋秋晴词即名《秋霁》。"}, {"id": 574, "name": "秋风清", "description": "一名《秋风引》。寇准词名《江南春》，刘长卿仄韵词名《新安路》。"}, {"id": 575, "name": "穆护砂", "description": "唐人张祐有五言绝句一首，题曰《穆护砂》，调名本此。盖因旧曲名，另倚新声也。"}, {"id": 576, "name": "章台柳", "description": "唐韩翃制，以首句为调名。"}, {"id": 577, "name": "端正好", "description": "杨无咎词名《于中好》。《中原音韵》注“正宫”。"}, {"id": 578, "name": "竹枝", "description": "唐教坊曲名。元郭茂倩《乐府诗集》云：“《竹枝》本出于巴渝。唐贞元中，刘禹锡在沅湘，以里歌鄙陋，乃依骚人《九歌》，作《竹枝》新词九章，教里中儿歌之。由是盛于贞元、元和之间。”"}, {"id": 579, "name": "竹香子", "description": "调见刘过《龙洲集》。"}, {"id": 580, "name": "竹马儿", "description": "一名《竹马子》。《乐章集》注“仙吕调”。"}, {"id": 581, "name": "笛家", "description": "一名《笛家弄慢》，柳永《乐章集》注“仙吕宫”。"}, {"id": 582, "name": "箇侬", "description": "调见廖莹中词，即用起句为名。"}, {"id": 583, "name": "簇水", "description": "调见《惜香乐府》。"}, {"id": 584, "name": "粉蝶儿", "description": "调见毛滂《东堂词》。因词有“粉蝶儿，这回共花同活”句，取以为名。金词注“中吕调”。《太和正音谱》：“中吕宫”。"}, {"id": 585, "name": "粉蝶儿慢", "description": "调见《片玉词》。"}, {"id": 586, "name": "系裙腰", "description": "调见张先词集。宋媛魏氏词名《芳草渡》。"}, {"id": 587, "name": "索酒", "description": "调见《松隐集》，自注“四时景物须酒之意。”"}, {"id": 588, "name": "紫玉箫", "description": "《宋史·乐志》：“歇指调”。"}, {"id": 589, "name": "紫萸香慢", "description": "调见凤林书院元词。姚云文自度腔。因词有“紫萸一枝传赐”句，取以为名。"}, {"id": 590, "name": "红林檎近", "description": "蒋氏《十三调》注“双调”。"}, {"id": 591, "name": "红窗听", "description": "柳永词注“仙吕调”。一名《红窗睡》。"}, {"id": 592, "name": "红窗迥", "description": "调见周邦彦《片玉词》。"}, {"id": 593, "name": "红罗袄", "description": "唐教坊曲名。"}, {"id": 594, "name": "红芍药", "description": "蒋氏《九宫谱目》入南吕调。"}, {"id": 595, "name": "纥那曲", "description": "明胡震亨《唐音癸签》云：“《纥那曲》，不知所出。考唐天宝中，崔成甫翻《得体歌》，有‘得体纥那也，纥囊得体那’之句，岂其所本欤？”"}, {"id": 596, "name": "纱窗恨", "description": "唐教坊曲名。毛文锡词有“月照纱窗，恨依依”句，取以为名。"}, {"id": 597, "name": "绕池游", "description": "调见《乐府雅词》。蒋氏《九宫谱》注“双调”。"}, {"id": 598, "name": "绛都春", "description": "蒋氏《九宫谱》注“黄钟宫”。此调有平韵、仄韵两体，宋词多填仄韵，其用平韵者，惟陈允平一词。"}, {"id": 599, "name": "绣停针", "description": "调见《放翁词》。"}, {"id": 600, "name": "绮寮怨", "description": "调见《片玉词》。"}, {"id": 601, "name": "绮罗香", "description": "调始《梅溪词》。"}, {"id": 602, "name": "绿盖舞风轻", "description": "调见《薲洲渔笛谱》，周密咏荷花自度曲也。"}, {"id": 603, "name": "缑山月", "description": "蒋氏《九宫谱目》入正宫引子。"}, {"id": 604, "name": "罥马索", "description": "调见《梅苑》。"}, {"id": 605, "name": "翠楼吟", "description": "姜夔自度夹钟商曲。"}, {"id": 606, "name": "翠羽吟", "description": "调见蒋捷《竹山词》。自序云：“王君本示予越调《小梅花引》，俾以飞仙步虚之意为其辞。余谓泛泛言仙，似乎寡味，越调之曲与梅花宜，罗浮梅花真仙事也，演以成章，名《翠羽吟》。”"}, {"id": 607, "name": "翻香令", "description": "此调始自苏轼，取词中第二句“惜香爱把宝钗翻”句为名。"}, {"id": 608, "name": "聒龙谣", "description": "调见朱敦儒《樵歌词》，因词有“聒龙啸”句，取以为名。"}, {"id": 609, "name": "胜州令", "description": "调见《花草粹编》。"}, {"id": 610, "name": "胜胜令", "description": "俞克成词名《声声令》。"}, {"id": 611, "name": "胡捣练", "description": "此调与《捣练子》异，或云似《桃源忆故人》，但前后段起句有押韵不押韵之分。惟《望仙楼》调本此减字，观《梅苑》刻《望仙楼》词仍名《胡捣练》，可知矣。"}, {"id": 612, "name": "腊梅香", "description": "此调有平韵、仄韵二体。仄韵者有吴、喻两词。平韵者止《梅苑》无名氏一词。"}, {"id": 613, "name": "舜韶新", "description": "宋王应麟《玉海》：“政和中，曹棐制征调《舜韶新》。”"}, {"id": 614, "name": "舞杨花", "description": "宋张端义《贵耳集》云：“慈宁殿赏牡丹，时椒房受册，三殿极欢，上洞达音律，自制曲，赐名《舞杨花》。停觞命小臣赋词，俾贵人歌以侑玉巵为寿，左右皆呼万岁。”"}, {"id": 615, "name": "舞马词", "description": "《唐书·礼乐志》:明皇尝命教舞马四百蹄，各为左右，分部目，衣以文绣，络以金珠，每千秋节舞于勤政楼下。赐宴设酺，其曲数十叠。马闻声奋首鼓尾，纵横应节。又施三层板牀，乘马而上，抃转如飞。或命壮士举榻，马舞其上，岁以为常。"}, {"id": 616, "name": "芙蓉月", "description": "调见《虚斋乐府》，盖咏芙蓉，因词中有“残月澹”句，故名《芙蓉月》。"}, {"id": 617, "name": "芭蕉雨", "description": "调见程垓《书舟词》。"}, {"id": 618, "name": "芰荷香", "description": "调见《大声集》。金词注“双调”。"}, {"id": 619, "name": "花上月令", "description": "宋吴文英自度曲。"}, {"id": 620, "name": "花前饮", "description": "调见宋杨湜《古今词话》，取词中前段结句为名。"}, {"id": 621, "name": "花发沁园春", "description": "此调有平韵、仄韵两体，俱见花庵《绝妙词选》，与《沁园春》不同。"}, {"id": 622, "name": "花发状元红慢", "description": "宋叶梦得《避暑录话》：“刘几在神宗时，与范蜀公重定大乐，洛阳花品曰‘状元红’，为一时之冠。乐工花日新能为新声，汴妓郜懿以色著，秘监致仕刘伯焘精音律。熙宁中，几携花日新就郜懿家赏花欢咏，乃撰此曲，填词以赠之。”"}, {"id": 623, "name": "花心动", "description": "金词注“小石调”。元词注“双调”。曹勛词名《好心动》，曹冠词名《桂飘香》，《鸣鹤余音》词名《上升花》，《高丽史·乐志》名《花心动慢》。"}, {"id": 624, "name": "花犯", "description": "调始《清真乐府》，周密词名《绣鸾凤花犯》。"}, {"id": 625, "name": "花非花", "description": "调见白居易《长庆集》。以首句为调名。"}, {"id": 626, "name": "芳草", "description": "晁补之词名《凤箫吟》。"}, {"id": 627, "name": "芳草渡", "description": "此调有两体。令词始自欧阳修，有张先词可校。慢词始自周邦彦，有陈允平词可校。"}, {"id": 628, "name": "苏幕遮", "description": "唐教坊曲名。按《唐书·宋务光传》，比见都邑坊市，相率为浑脱队，骏马戎服，名《苏幕遮》。\t又按张说集有《苏幕遮》七言绝句，宋词盖因旧曲名，另度新声也。周邦彦词有“鬓云松”句，更名《鬓云松令》。金词注“般涉调”。"}, {"id": 629, "name": "茅山逢故人", "description": "调见元人《叶儿乐府》，张雨句曲道中送友，自制词也。"}, {"id": 630, "name": "茶瓶儿", "description": "调见《花庵词选》。始自北宋李元膺，至南宋赵彦端、石孝友二家，又摊破两结句法，减去两起句字，自成新声。"}, {"id": 631, "name": "荔子丹", "description": "调见《高丽史·乐志》。"}, {"id": 632, "name": "荔枝香", "description": "《唐史·乐志》：“帝幸骊山，贵妃生日，命小部张乐长生殿，奏新曲，未有名，会南方进荔枝，因名《荔枝香》。”《碧鸡漫志》：“今歇指调、大石调，皆有近拍，不知何者为本曲。”按《荔枝香》有两体，七十六字者始自柳永，《乐章集》注“歇指调”，有周邦彦、方千里、杨泽民、陈允平及吴文英词可校。七十三字者始自周邦彦，有方千里、杨泽民、陈允平和词及袁去华词可校，一名《荔枝香近》。"}, {"id": 633, "name": "荷华媚", "description": "调见《东坡词集》，即赋题本意也。"}, {"id": 634, "name": "荷叶杯", "description": "唐教坊曲名。此词有单调、双调。单调者有温庭筠、顾夐二体，双调者只韦庄一体，俱见《花间集》。"}, {"id": 635, "name": "荷叶铺水面", "description": "调见《花草粹编》。"}, {"id": 636, "name": "莺啼序", "description": "一名《丰乐楼》，见《梦窗乙稿》。"}, {"id": 637, "name": "菊花新", "description": "《乐章集》注“中吕调”。《齐东野语》云：“《菊花新》谱，教坊都管王公谨作也。”"}, {"id": 638, "name": "菩萨蛮", "description": "唐教坊曲名。《宋史·乐志》：女弟子舞队名。《尊前集》注“中吕宫”。《宋史·乐志》亦“中吕宫”。《正音谱》注“正宫”。唐苏鄂《杜阳杂编》云：“大中初，女蛮国入贡，危髻金冠，缨络被体，号菩萨蛮队，当时倡优遂制《菩萨蛮》曲，文士亦往往声其词。”孙光宪《北梦琐言》云：“唐宣宗爱唱《菩萨蛮》词，令狐绹命温庭筠新撰进之。”《碧鸡漫志》云：“今《花间集》温词十四首是也。”"}, {"id": 639, "name": "菩萨蛮慢", "description": "调见凤林书院元词。一名《菩萨蛮引》，与《菩萨蛮》令词不同。"}, {"id": 640, "name": "落梅", "description": "《梅苑》无名氏词名《落梅慢》。"}, {"id": 641, "name": "落梅风", "description": "调见《梅苑》。按《梅苑》别有《落梅风》长调二首，俱一百六字，因《花草粹编》名《落梅》，亦名《落梅慢》，另编一体，不为类列。"}, {"id": 642, "name": "蓦山溪", "description": "《翰墨全书》名《上阳春》。金词注“大石调”。"}, {"id": 643, "name": "蕃女怨", "description": "唐温庭筠二词，俱咏蕃女之怨，故词中有“雁门沙碛”诸语。"}, {"id": 644, "name": "蕊珠閒", "description": "调见《介庵词》。"}, {"id": 645, "name": "蕙兰芳引", "description": "调见《清真乐府》，方千里、杨泽民、陈允平俱有和词。杨词一名《蕙兰芳》，无“引”字。"}, {"id": 646, "name": "薄倖", "description": "调见《东山乐府》。"}, {"id": 647, "name": "薄媚摘遍", "description": "沈括《梦溪笔谈》：“所谓大遍者，凡数十解，每解有数叠。裁截用之，则谓之摘遍。”按《薄媚》大曲凡十遍，此盖摘其《入破》之一遍也。"}, {"id": 648, "name": "虞美人", "description": "唐教坊曲名。《碧鸡漫志》云：“《虞美人》旧曲三，其一属中吕调，其一属中吕宫，近世又转入黄钟宫。”元高拭词注“南吕调”。《乐府雅词》名《虞美人令》。周紫芝词有“只恐怕寒，难近玉壶冰”句，名《玉壶冰》。张炎词赋柳儿，因名《忆柳曲》。王行词取李煜“恰似一江春水向东流”句，名《一江春水》。"}, {"id": 649, "name": "蜀溪春", "description": "调见《松隐集》，咏黄蔷薇花。因词有“蜀景风迟，浣花溪边”，“占上苑，留住春”句，取以为名。"}, {"id": 650, "name": "蝴蝶儿", "description": "调见《花间集》。取词中起句为名。"}, {"id": 651, "name": "蝶恋花", "description": "唐教坊曲。本名《鹊踏枝》，宋晏殊词改今名。《乐章集》注“小石调”，赵令畤词注“商调”，《太平乐府》注“双调”。冯延巳词有“杨柳风轻，展尽黄金缕”句，名《黄金缕》。赵令畤词有“不卷珠帘，人在深深院”句，名《卷珠帘》。司马槱词有“夜凉明月生南浦”句，名《明月生南浦》。韩淲词有“细雨吹池沼”句，名《细雨吹池沼》。贺铸词名《凤栖梧》，李石词名《一箩金》，衷元吉词名《鱼水同欢》，沈会宗词名《转调蝶恋花》。"}, {"id": 652, "name": "行香子", "description": "《中原音韵》、《太平乐府》俱注“双调”。蒋氏《九宫谱目》入中吕引子。"}, {"id": 653, "name": "行香子慢", "description": "调见《高丽史·乐志》，此《行香子》慢词，与《行香子》小令不同。"}, {"id": 654, "name": "被花恼", "description": "杨缵自度曲，因词中有“被花恼”句，取以为名。"}, {"id": 655, "name": "西吴曲", "description": "调见《龙洲集》。"}, {"id": 656, "name": "西地锦", "description": "元高拭词，第三句七字者，注“黄钟宫”。"}, {"id": 657, "name": "西子妆", "description": "张炎词序：“吴梦窗自制此曲。”或加“慢”字。"}, {"id": 658, "name": "西平乐", "description": "此调有仄韵、平韵两体。仄韵者始自柳永，《乐章集》注“小石调”。平韵者始自周邦彦，一名《西平乐慢》。"}, {"id": 659, "name": "西施", "description": "《乐章集》注“仙吕调”。"}, {"id": 660, "name": "西江月", "description": "唐教坊曲名。《乐章集》注“中吕宫”。欧阳炯词有“两岸苹香暗起”句，名《白苹香》。程珌词名《步虚词》。王行词名《江月令》。"}, {"id": 661, "name": "西江月慢", "description": "调见《圣求词》。"}, {"id": 662, "name": "西河", "description": "《碧鸡漫志》：“大石调《西河慢》，声犯正平。”张炎词名《西湖》。"}, {"id": 663, "name": "西湖月", "description": "调见凤林书院元词，黄子行自度商调曲。"}, {"id": 664, "name": "西溪子", "description": "唐教坊曲名。"}, {"id": 665, "name": "角招", "description": "调见赵以夫《虚斋集》，自注：“姜夔制《角招》、《征招》二曲，余以《角招》赋梅。古乐府有大、小《梅花》，皆角声也。”"}, {"id": 666, "name": "解佩令", "description": "调见《小山乐府》。按《楚辞》“捐予佩兮澧浦”，《韩诗外传》“郑交甫遇汉皋神女解佩”，调名取此。"}, {"id": 667, "name": "解红", "description": "按《宋史·乐志》，小儿舞队有《解红》，其曲失传。陈旸《乐书》载和凝作，乃唐词也。若《鸣鹤余音》有《解红儿慢》，系元人所制，与此不同。"}, {"id": 668, "name": "解红慢", "description": "调见《鸣鹤余音》。"}, {"id": 669, "name": "解语花", "description": "王行词注“林钟羽”。"}, {"id": 670, "name": "解蹀躞", "description": "曹勛词名《玉蹀躞》。"}, {"id": 671, "name": "解连环", "description": "此调始自柳永，以词有“信早梅、偏占阳和”及“时有香来，望明艳、遥知非雪”句，名《望梅》。后因周邦彦词有“妙手能解连环”句，更名《解连环》。张辑词有“把千种旧愁，付与杏梁雨燕”句，又名《杏梁燕》。"}, {"id": 672, "name": "诉衷情", "description": "唐教坊曲名。毛文锡词有“桃花流水漾纵横”句，又名《桃花水》。"}, {"id": 673, "name": "诉衷情令", "description": "《乐章集》注“林钟商”。张元干以黄庭坚词曾咏“渔父家风”，改名《渔父家风》。张辑词有“一钓丝风”句，名《一丝风》。"}, {"id": 674, "name": "诉衷情近", "description": "调见《乐章集》，注“林钟商”。与《诉衷情令》不同。"}, {"id": 675, "name": "误桃源", "description": "宋张耒《明道杂志》云：“掌禹锡学士考试太学生，出《砥柱勒铭赋》题，此铭今俱在，乃唐太宗铭禹功，而掌公误记为太宗自铭其功。宋涣中第一，其赋悉是太宗自铭，有无名子作此嘲之。”"}, {"id": 676, "name": "调笑令", "description": "古调笑。《乐苑》：“商调曲”。一名《宫中调笑》。白居易诗“打嫌调笑易”，自注：“调笑，抛打曲名也。”戴叔伦词名《转应曲》，冯延巳词名《三台令》，与宋词《调笑令》不同。"}, {"id": 677, "name": "谒金门", "description": "唐教坊曲名。元高拭词注“商调”。宋杨湜《古今词话》，因韦庄词起句，名《空相忆》。张辑词有“无风花自落”句，名《花自落》。又有“楼外垂杨如此碧”句，名《垂杨碧》。李清臣词有“杨花落”句，名《杨花落》。李石名《出塞》。韩淲词有“东风吹酒面”句，名《东风吹酒面》。又有“不怕醉，记取吟边滋味”句，名《不怕醉》。又有“人已醉，溪北溪南春意，击鼓吹萧花落未”句，名《醉花春》。又有“春尚早，春入湖山渐好”句，名《春早湖山》。"}, {"id": 678, "name": "谢池春", "description": "李石词名《风中柳》，《高丽史》无名氏词名《风中柳令》，孙道绚词名《玉莲花》，黄澄词名《卖花声》。"}, {"id": 679, "name": "谢池春慢", "description": "调见《古今词话》，张先玉仙观道中逢谢媚卿作，盖慢词也，与六十六字《谢池春》令词不同。"}, {"id": 680, "name": "贺圣朝", "description": "唐教坊曲名。《花间集》有欧阳炯词，本名《贺明朝》，《词律》混入《贺圣朝》误。"}, {"id": 681, "name": "贺新郎", "description": "叶梦得词有“唱金缕”句，名《金缕歌》，又名《金缕曲》，又名《金缕词》。苏轼词有“乳燕飞华屋”句，名《乳燕飞》，有“晚凉新浴”句，名《贺新凉》，有“风敲竹”句，名《风敲竹》。张辑词有“把貂裘、换酒长安市”句，名《貂裘换酒》。"}, {"id": 682, "name": "贺熙朝", "description": "调见《花间集》。"}, {"id": 683, "name": "赏南枝", "description": "调见《梅苑》词，曾巩自度曲。"}, {"id": 684, "name": "赏松菊", "description": "调见曹勛《松隐集》。"}, {"id": 685, "name": "赞成功", "description": "调见《花间集》。"}, {"id": 686, "name": "赞浦子", "description": "唐教坊曲名。一名《赞普子》。"}, {"id": 687, "name": "赤枣子", "description": "唐教坊曲名。"}, {"id": 688, "name": "越江吟", "description": "按宋释文莹《续湘山野录》云：“太宗酷爱琴曲十小词，命近臣十人各探一调、撰一词，苏翰林易简探得《越江吟》，遂赋此词。”后贺铸词因苏词起句有“瑶池宴”字，更名《宴瑶池》。苏轼词名《瑶池宴》。《乐府雅词》名《瑶池宴令》。"}, {"id": 689, "name": "越溪春", "description": "调见《六一居士词》，因词中有“春色遍天涯，越溪阆苑繁华地”句，取以为名，盖赋越溪春色也。"}, {"id": 690, "name": "踏歌", "description": "调见《太平樵唱》词，又见《梅苑群贤词》，与唐人小令《踏歌行》不同。"}, {"id": 691, "name": "踏歌词", "description": "唐《辇下岁时记》：先天初，上御安福门观灯，令朝士能文者为《踏歌》。陈旸《乐书》云：“《踏歌》，队舞曲也。”"}, {"id": 692, "name": "踏莎行", "description": "金词注“中吕调”。曹冠词名《喜朝天》，越长卿词名《柳长春》，《鸣鹤余音》词名《踏雪行》。曾觌、陈亮词添字者，名《转调踏莎行》。"}, {"id": 693, "name": "踏青游", "description": "调见苏轼词，踏青作也。因词有“踏青游”句，取以为名。"}, {"id": 694, "name": "轮台子", "description": "柳永《乐章集》注“中吕调”。"}, {"id": 695, "name": "辊绣毬", "description": "调见《惜香乐府》。"}, {"id": 696, "name": "过涧歇", "description": "《乐章集》注“中吕调”。"}, {"id": 697, "name": "过秦楼", "description": "调见《乐府雅词》，李甲作。因词有“曾过秦楼”句，取以为名。"}, {"id": 698, "name": "迎新春", "description": "《宋史·乐志》：“双角调”。《乐章集》注“大石调”。"}, {"id": 699, "name": "迎春乐", "description": "宋柳永词注“林钟商”，元王行词注“夹钟商”。"}, {"id": 700, "name": "还京乐", "description": "唐教坊曲名。《唐书》：“明皇自潞州还京师，制《还京乐》曲。”宋词盖借旧曲名，另翻新声也。"}, {"id": 701, "name": "远朝归", "description": "调见《梅苑》词。"}, {"id": 702, "name": "连理枝", "description": "《尊前集》注“黄钟宫”。《宋史·乐志》：“琵琶曲，蕤宾调。”程垓词名《红孃子》。刘过词名《小桃红》，又名《灼灼花》。"}, {"id": 703, "name": "迷仙引", "description": "《乐章集》注“双调”。"}, {"id": 704, "name": "迷神引", "description": "《乐章集》注“中吕调”。"}, {"id": 705, "name": "送入我门来", "description": "调见《草堂诗余》。宋胡浩然除夕词有“东风尽力，一齐吹送，入此门来”之句，取以为名。"}, {"id": 706, "name": "送征衣", "description": "柳永《乐章集》注“中吕宫”。"}, {"id": 707, "name": "选冠子", "description": "一名《选官子》。曹勛词名《转调选冠子》。鲁逸仲词名《惜余春慢》。侯寘词名《苏武慢》，一名《仄韵过秦楼》。"}, {"id": 708, "name": "逍遥乐", "description": "调见黄庭坚《琴趣外篇》，即赋本意。"}, {"id": 709, "name": "透碧霄", "description": "柳永《乐章集》注“南吕宫”。"}, {"id": 710, "name": "遍地锦", "description": "调见毛滂《东堂词》，孙守席上咏牡丹花作也。《花草粹编》注“小石调”。"}, {"id": 711, "name": "遐方怨", "description": "唐教坊曲名。此调有两体，单调者始于温庭筠，双调者始于顾夐、孙光宪，惟《花间集》有之，宋人无填此者。"}, {"id": 712, "name": "遥天奉翠华引", "description": "调见《孏窟词》。"}, {"id": 713, "name": "遶佛阁", "description": "调见《清真乐府》。"}, {"id": 714, "name": "遶池游慢", "description": "调见《涧泉词》，韩淲西湖看荷作。"}, {"id": 715, "name": "郭郎儿近拍", "description": "调见《乐章集》，注“仙吕调”。按《乐府杂录》，傀儡子戏，其引歌舞有郭郎者，善优笑，闾里呼为郭郎，凡戏场必在俳儿之首。柳词调名或取诸此。"}, {"id": 716, "name": "酒泉子", "description": "唐教坊曲名。"}, {"id": 717, "name": "酷相思", "description": "调见《书舟雅词》。"}, {"id": 718, "name": "醉乡春", "description": "宋惠洪《冷斋夜话》云：“少游在黄州，饮于海棠桥，桥南北多海棠，有书生家于海棠丛间。少游醉宿于此，题词壁间。”按此则知此调创自秦观，因后结有“醉乡广大人间小”句，故名《醉乡春》。又因前结有“春色又添多少”句，一名《添春色》。"}, {"id": 719, "name": "醉公子", "description": "唐教坊曲名。薛昭蕴、顾夐词，俱四换韵，一名《四换头》。此调有两体，四十字者昉自唐人。一百六字者昉自宋人。"}, {"id": 720, "name": "醉吟商", "description": "姜夔自序云：“石湖老人为予言，琵琶有四曲，今不传矣，曰《濩索·凉州》、《转关·绿腰》、《醉吟商·胡渭州》、《历絃·薄媚》也。予每念之。辛亥夏，谒杨廷秀于金陵邸中，遇琵琶工，解作《醉吟商·胡渭州》，因求得品絃法，译成《醉吟商》小令，实双调也。”"}, {"id": 721, "name": "醉垂鞭", "description": "词见张先集。"}, {"id": 722, "name": "醉太平", "description": "一名《凌波曲》。孙惟信词名《醉思凡》。周密词名《四字令》。《太平乐府》注“南吕宫”。《太和正音谱》注“正宫，又入仙吕宫、中吕宫。”"}, {"id": 723, "name": "醉妆词", "description": "唐孙光宪《北梦琐言》：“蜀王衍尝裹小巾，其尖如锥。宫人皆衣道服，簪莲花冠，施胭脂夹脸，号‘醉妆’，因作《醉妆词》。”"}, {"id": 724, "name": "醉思仙", "description": "调见吕渭老词，因词有“怎惯不思量”及“当时醉倒残缸”句，取以为名。"}, {"id": 725, "name": "醉春风", "description": "赵鼎词名《怨东风》。《太平乐府》、《中原音韵》俱入中吕类。《太和正音谱》注“中吕宫，亦入正宫，又入双调”。蒋氏《十三调》注“中吕调”。"}, {"id": 726, "name": "醉红妆", "description": "调见张先词集，因词中有“一般妆样百般娇”及“郎未醉，有金貂”句，取以为名。"}, {"id": 727, "name": "醉翁操", "description": "琴曲，属正宫。苏轼自序：琅邪幽谷，山川奇丽，泉鸣空涧，若中音会。醉翁喜之，把酒临听，辄欣然忘归。既去十余年，好奇之士沈遵闻之往游，以琴写其声，曰《醉翁操》。然有声而无词，好事者倚其声制曲，粗合拍度，而琴声为词所绳约，非天成也。后三十年，翁既捐馆舍，遵亦殁，有庐山玉涧道人崔閒，妙于琴，恨此曲之无词，乃谱其声，而请东坡居士补之云。"}, {"id": 728, "name": "醉花间", "description": "唐教坊曲名。《宋史·乐志》：双调。"}, {"id": 729, "name": "醉花阴", "description": "《中原音韵》注“黄钟宫”。《太平乐府》注“中吕宫”。"}, {"id": 730, "name": "醉蓬莱", "description": "《乐章集》注“林钟商”。赵磻老词有“璧月流光，雪消寒峭”句，名《雪月交光》。韩淲词有“玉作山前，冰为水际，几多风月”句，名《冰玉风月》。"}, {"id": 731, "name": "醉高歌", "description": "元姚燧自度曲。《太平乐府》注“中吕宫”。"}, {"id": 732, "name": "采明珠", "description": "曹植《洛神赋》：“或采明珠”，调名取此。《宋史·乐志》：“曲破中吕调，《采明珠》。”"}, {"id": 733, "name": "采桑子", "description": "唐教坊曲有《杨下采桑》，调名本此。《尊前集》注“羽调”。《乐府雅词》注“中吕宫”。南唐李煜词名《丑奴儿令》，冯延巳词名《罗敷媚歌》，贺铸词名《丑奴儿》，陈师道词名《罗敷媚》。"}, {"id": 734, "name": "采桑子慢", "description": "一名《丑奴儿慢》。潘元质词有“愁春未醒”句，亦名《愁春未醒》。辛弃疾词名《丑奴儿近》。《花草粹编》无名氏词名《叠青钱》。"}, {"id": 735, "name": "采绿吟", "description": "宋周密自度曲，取词中起句二字为调名。"}, {"id": 736, "name": "采莲令", "description": "按《宋史·乐志》，曲宴游幸，教坊所奏十八调曲，九曰《双调·采莲》，今柳永《乐章集》有之，亦注“双调”。《碧鸡漫志》：“夹钟商，俗呼双调”。"}, {"id": 737, "name": "采莲子", "description": "唐教坊曲名。"}, {"id": 738, "name": "金人捧露盘", "description": "一名《铜人捧露盘》。程垓词名《上平西》，张元干词名《上西平》，又名《西平曲》。刘昂词名《上平南》。金词注“越调”。"}, {"id": 739, "name": "金凤鈎", "description": "见晁补之《琴趣外篇》。此调微近《夜行船》，其实不同也。"}, {"id": 740, "name": "金字经", "description": "《太平乐府》注“南吕宫”。《元史·乐志》说法舞队有《金字经》曲，一名《阅金经》。"}, {"id": 741, "name": "金明池", "description": "调见《淮海词》，赋东京金明池，即以调为题也。李弥逊词名《昆明池》，僧挥词名《夏云峰》。"}, {"id": 742, "name": "金浮图", "description": "调见《尊前集》。"}, {"id": 743, "name": "金盏倒垂莲", "description": "此调有平韵、仄韵两体。平韵者见晁无咎《琴趣外篇》及《梅苑》词。仄韵者见《松隐词》。"}, {"id": 744, "name": "金盏子", "description": "此调有平韵、仄韵两体。仄韵者见《梅溪词》及《梦窗词》，平韵者见《高丽史·乐志》。"}, {"id": 745, "name": "金盏子令", "description": "见《高丽史·乐志》。"}, {"id": 746, "name": "金莲绕凤楼", "description": "调见《花草粹编》。此宋徽宗观灯词也，故名《金莲绕凤楼》。"}, {"id": 747, "name": "金菊对芙蓉", "description": "蒋氏《九宫谱》：中吕引子。"}, {"id": 748, "name": "金蕉叶", "description": "此调始自柳永，因词中有“金蕉叶泛金波霁”句，取以为名。袁去华、蒋捷词皆从柳词减字。《乐章集》注“大石调”。元高拭词注“越调”。"}, {"id": 749, "name": "金错刀", "description": "汉张衡诗“美人赠我金错刀”，调名本此。此调见《花草粹编》，一名《醉瑶瑟》。叶李押仄韵词，名《君来路》。"}, {"id": 750, "name": "钗头凤", "description": "撷芳词。《古今词话》云：“政和间，京师妓之姥曾嫁伶官，常入内教舞，传禁中《撷芳词》以教其妓。人皆爱其声，又爱其词，类唐人所作。张尚书帅成都，蜀中传此词，竞唱之。却于前段下添‘忆忆忆’三字，后段下添‘得得得’三字，又名《摘红英》，殊失其义。不知禁中有“撷芳园”，故名“撷芳词”也。”"}, {"id": 751, "name": "钿带长中调", "description": "调见《大声集》，即咏钿带香囊本意。"}, {"id": 752, "name": "锁寒窗", "description": "一名《锁寒窗》，调见《片玉集》，盖寒食词也。因词有“静锁一庭愁雨”及“故人剪烛西窗雨”句，取以为名。"}, {"id": 753, "name": "锁窗寒", "description": "一名《锁寒窗》，调见《片玉集》，盖寒食词也。因词有“静锁一庭愁雨”及“故人剪烛西窗雨”句，取以为名。"}, {"id": 754, "name": "锦园春", "description": "调见《全芳备祖·乐府》"}, {"id": 755, "name": "锦堂春慢", "description": "调见《青箱杂记》，《梅苑》词名《锦堂春》。"}, {"id": 756, "name": "锦帐春", "description": "调见《稼轩集》。因词有“春色难留”及“重帘不卷，翠屏天远”句，故名。"}, {"id": 757, "name": "锦缠道", "description": "《全芳备祖·乐府》名《锦缠头》，江衍词名《锦缠绊》，原注“黄钟宫”。"}, {"id": 758, "name": "锯解令", "description": "调见《逃禅词》。"}, {"id": 759, "name": "长亭怨慢", "description": "姜夔自度中吕宫曲，或作《长亭怨》，无“慢”字。"}, {"id": 760, "name": "长命女", "description": "唐教坊曲名。杜佑《理道要诀》：《长命女》在林钟羽，时号平调，今俗呼高平调。《碧鸡漫志》：《长命女令》，前七拍，后九拍，属仙吕调。按仙吕调即夷则羽，皆羽声也。和凝词名《薄命女》。"}, {"id": 761, "name": "长寿乐", "description": "《宋史·乐志》：“仙吕调”。《乐章集》注“平调”。"}, {"id": 762, "name": "长寿仙", "description": "调见《松雪集》。"}, {"id": 763, "name": "长生乐", "description": "调见《珠玉集》。"}, {"id": 764, "name": "长相思", "description": "唐教坊曲名。林逋词有“吴山青”句，名《吴山青》。张辑词有“江南山渐青”句，名《山渐青》。王行词名《青山相送迎》。《乐府雅词》名《长相思令》，又名《相思令》。"}, {"id": 765, "name": "长相思慢", "description": "《乐章集》注“商调”。"}, {"id": 766, "name": "閒中好", "description": "调见唐段成式《酉阳杂俎》，有平韵、仄韵二体，即以首句三字为调名也。"}, {"id": 767, "name": "阮郎归", "description": "宋丁持正词有“碧桃春昼长”句，名《碧桃春》。李祁词名《醉桃源》。曹冠词名《宴桃源》。韩淲词有“濯缨一曲可流行”句，名《濯缨曲》。"}, {"id": 768, "name": "阳关引", "description": "此调始自宋寇准词，本檃括王维《阳关曲》而作，故名。晁补之词名《古阳关》。"}, {"id": 769, "name": "阳关曲", "description": "本名《渭城曲》。宋秦观云：“《渭城曲》绝句，近世又歌入《小秦王》，更名《阳关曲》。属双调，又属大石调。”"}, {"id": 770, "name": "阳台梦", "description": "此调有两体，四十九字者调见《尊前集》，唐庄宗制，因词有“又入阳台梦”句，取以为名。五十七字者调见《花草粹编》，宋解昉制，即赋阳台梦题。两体截然不同。"}, {"id": 771, "name": "阳台路", "description": "《乐章集》注“林钟商”。"}, {"id": 772, "name": "阳春", "description": "一名《阳春曲》。"}, {"id": 773, "name": "陌上花", "description": "《东坡词话》：“钱塘人好唱《陌上花》、《缓缓曲》，盖吴越王遗事也。”调名取此。"}, {"id": 774, "name": "隔帘听", "description": "唐教坊曲名。《乐章集》注“林钟商”。"}, {"id": 775, "name": "隔浦莲近拍", "description": "唐《白居易集》有《隔浦莲》曲，调名本此。一名《隔浦莲》，又名《隔浦莲近》。"}, {"id": 776, "name": "雨中花令", "description": "王观词名《送将归》。按《雨中花》调与《夜行船》调最易相混，宋人集中每多误刻。今照《花草粹编》所编，以两结句五字者为《雨中花》。两结句六字、七字者为《夜行船》。"}, {"id": 777, "name": "雨中花慢", "description": "此词有平韵、仄韵两体。平韵者始自苏轼，仄韵者始自秦观。柳永平韵词，《乐章集》注“林钟商”。"}, {"id": 778, "name": "雨霖铃", "description": "一名《雨霖铃慢》，唐教坊曲名。《明皇杂录》：“帝幸蜀，初入斜谷，霖雨弥日，栈道中闻铃声，采其声为《雨霖铃》曲。”宋词盖借旧曲名，另倚新声也。调见柳永《乐章集》，属双调。"}, {"id": 779, "name": "雪夜渔舟", "description": "调见《虚靖真人词》，因词中有“自棹孤舟，顺流观雪”句，取以为名。"}, {"id": 780, "name": "雪明鳷鹊夜", "description": "调见《花草粹编》。"}, {"id": 781, "name": "雪梅香", "description": "《乐章集》注“正宫”。"}, {"id": 782, "name": "雪狮儿", "description": "调见《书舟集》。"}, {"id": 783, "name": "雪花飞", "description": "《宋史·乐志》：“高角调”。按高角乃大吕之角声也。"}, {"id": 784, "name": "霓裳中序第一", "description": "唐白居易《霓裳羽衣舞歌》云：“散序六奏未动衣，阳台宿云慵不飞。中序擘騞初入拍，秋竹吹裂春冰坼。”自注云：“散序六遍无拍，故不舞。中序始有拍，亦名拍序。”宋沈括《笔谈》云：“《霓裳曲》凡十二叠，前六叠无拍，至第七叠方谓之叠遍，自此始有拍而舞。”按此知《霓裳曲》十二叠，至七叠中序始舞，故以第七叠为中序第一，盖舞曲之第一遍也。"}, {"id": 785, "name": "霜叶飞", "description": "调见《片玉集》，因词有“素娥青女鬭婵娟”句，更名《鬭婵娟》。"}, {"id": 786, "name": "霜天晓角", "description": "元高拭词注“越调”。张辑词有“一片月当窗白”句，名《月当窗》。程垓词有“须共踏月深夜”，名《踏月》。吴礼之词有“长桥月”句，名《长桥月》。"}, {"id": 787, "name": "霜花腴", "description": "吴文英自度腔，因词有“霜饱花腴”句，取以为名。"}, {"id": 788, "name": "露华", "description": "唐李白《清平调》词：“东风拂槛露华浓”，调名本此。按此调有仄韵、平韵两体，周密平韵词名《露华慢》。"}, {"id": 789, "name": "青玉案", "description": "汉张衡诗“何以报之青玉案”，调名取此。《中原音韵》注“双调”。《太和正音谱》注“高平调”。蒋氏《九宫谱目》入中吕引子。韩淲词有“苏公堤上西湖路”句，名《西湖路》。"}, {"id": 790, "name": "青门引", "description": "调见《乐府雅词》及《天机余锦》词，张先本集不载。"}, {"id": 791, "name": "青门饮", "description": "调见《淮海词》。黄裳词亦名《青门引》，然与《青门引》令词不同。"}, {"id": 792, "name": "鞓红", "description": "调见《梅苑》。"}, {"id": 793, "name": "韵令", "description": "按唐《教坊记》有上韵、中韵、下韵三小韵，《韵令》调名疑出于此。宋周辉《清波杂志》云：“宣和间，衣著曰韵襭，果实曰韵梅，词曲曰韵令。”张世南《游宦纪闻》云：“宣和间，市井竞唱《韵令》。”"}, {"id": 794, "name": "风光好", "description": "调见《本事曲》，陶榖作。"}, {"id": 795, "name": "风入松", "description": "古琴曲有《风入松》。唐僧皎然有《风入松》歌，见《乐府诗集》，调名本此。《宋史·乐志》注“林钟商”。元高拭词注“仙吕调”，又“双调”。蒋氏《十三调》注“双调”。亦名《风入松慢》。韩淲词有“小楼春映远山横”句，名《远山横》。"}, {"id": 796, "name": "风流子", "description": "唐教坊曲名。单调者，唐词一体。双调者，宋词三体。有前后段两起句不用韵者，有前段起句用韵、后段起句不用韵者，有前后段起句俱用韵者，诸体中有句读异同，各依其体类列。"}, {"id": 797, "name": "飞雪满群山", "description": "调见《友古词》。因词有“长记得、扁舟寻旧约”句，更名《扁舟寻旧约》。张榘词名《飞雪满堆山》。"}, {"id": 798, "name": "飞龙宴", "description": "调见《花草粹编》，注“吴七君王姬苏小孃制。”"}, {"id": 799, "name": "饮马歌", "description": "调见《松隐集》。自序：此曲自金源传至边城，饮牛马即横笛吹之，不鼓不拍，声甚凄断。"}, {"id": 800, "name": "马家春慢", "description": "调见《东山乐府》。"}, {"id": 801, "name": "驻马听", "description": "《乐章集》注“林钟商”。"}, {"id": 802, "name": "高山流水", "description": "调见《梦窗词》。吴文英自度曲，赠丁基仲妾作也。妾善琴，故以《高山流水》为调名。"}, {"id": 803, "name": "高平探芳新", "description": "调见《梦窗词》，吴文英自度高平调曲。"}, {"id": 804, "name": "高阳台", "description": "高拭词注“商调”。刘镇词名《庆春泽慢》，王沂孙词名《庆春宫》。"}, {"id": 805, "name": "鬓边华", "description": "调见《梅苑》词。因词中有“映青鬓、开人醉眼”句，取以为名。"}, {"id": 806, "name": "鬭百花", "description": "《乐章集》注“正宫“。晁补之词一名《夏州》。"}, {"id": 807, "name": "鬲溪梅令", "description": "姜夔自度曲，注“宫调”。原注“仙吕调”。一作《高溪梅令》。"}, {"id": 808, "name": "鱼游春水", "description": "《复斋漫录》：”政和中，一中贵使越州回，得词于古碑，无名无谱，录以进御，命大晟府填腔，因词中语，赐名《鱼游春水》。”"}, {"id": 809, "name": "鹊桥仙", "description": "此调有两体。五十六字者始自欧阳修，因词中有“鹊迎桥路接天津”句，取为调名。周邦彦词名《鹊桥仙令》，《梅苑》词名《忆人人》。韩淲词取秦观词句，名《金风玉露相逢曲》，张辑词有“天风吹送广寒秋”句，名《广寒秋》。元高拭词注“仙吕调”。八十八字者始自柳永，《乐章集》注云：“歇指调”。"}, {"id": 810, "name": "鹤冲天", "description": "调见柳永《乐章集》。“閒窗漏永”词注“大石调”。“黄金榜上”词注“正宫”。与《喜迁莺》、《春光好》别名《鹤冲天》者不同。"}, {"id": 811, "name": "鹦鹉曲", "description": "一名《黑漆弩》，又名《学士吟》。白无咎词有“侬家鹦鹉洲边住”句，故名《鹦鹉曲》。《太平乐府》注“正宫”。"}, {"id": 812, "name": "鹧鸪天", "description": "《乐章集》注“正平调”。《太和正音谱》注“大石调”。蒋氏《九宫谱目》入仙吕引子。赵令畤词名《思越人》，李元膺词名《思佳客》。贺铸词有“剪刻朝霞钉露盘”句，名《剪朝霞》。韩淲词有“只唱骊歌一叠休”句，名《骊歌一叠》。卢祖皋词有“人醉梅花卧未醒”句，名《醉梅花》。"}, {"id": 813, "name": "麦秀两岐", "description": "唐教坊曲名。《碧鸡漫志》云，属黄钟宫。"}, {"id": 814, "name": "黄河清慢", "description": "《铁围山丛谈》云：“宣和初，燕乐初成，八音告备，有曲名《黄河清》，音调极韶美，天下无问遐迩大小，皆爭唱之。”"}, {"id": 815, "name": "黄莺儿", "description": "调见《乐章集》，原注“正宫”。即咏黄莺儿，取以为名。"}, {"id": 816, "name": "黄钟乐", "description": "唐教坊曲名。"}, {"id": 817, "name": "黄鹂绕碧树", "description": "调见《清真乐府》。"}, {"id": 818, "name": "黄鹤引", "description": "宋方勺《泊宅编》云：“先子晚官邓州，一日秋风起，思吴中山水，尝信笔作长短句，序云：‘阮田曹所制《黄鹤引》，爱其词调清高，寄为一阕，命稚子歌之，以侑尊焉。’”"}, {"id": 819, "name": "黄鹤洞仙", "description": "调见元彭致中《鸣鹤余音》词。"}, {"id": 820, "name": "鼓笛令", "description": "调见《黄山谷集》。按宋词有《鼓笛慢》，乃《水龙吟》别体，与此无涉。"}, {"id": 821, "name": "齐天乐", "description": "周密《天基节乐次》：“乐奏夹钟宫，第一盏，觱篥起《圣寿齐天乐慢》。”姜夔词注“黄钟宫“，俗名正宫。周邦彦词有“绿芜彫尽台城路”句，名《台城路》。沈端节词名《五福降中天》。张辑词有“如此江山”句，名《如此江山》。"}, {"id": 822, "name": "龙山会", "description": "《虚斋乐府》注“商调”。"}]
